INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('2025 문동균 한국사 판서노트 1/2 특강', '최근 출제 경향을 반영한 기출 사료와 기출 선지로 구성된 교재이다. 최신 기출문제를 모두 분석하였으며, 『올인원 기본서』와 『판서노트』의 학습 내용과 더불어 심화 학습을 할 수 있도록 하였다. 각 기출 사료에 대한 문쌤의 분석과 팁을 실었다.', '문동균 (지은이)', '에스티유니타스', 'https://image.aladin.co.kr/product/34384/99/cover200/k712932404_1.jpg', '국내도서>수험서/자격증>공무원 수험서>7/9급 공무원>한국사>강의노트/기타', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('우연한 미래에 우리가 있어서', '슬픔에 적극적으로 침잠함으로써 서정과 사회를 연결해온 시인 신용목의 일곱번째 시집. 서른한 편의 시가 총 여덟 부로 나뉘어 실려 있다. 첫 시집 『그 바람을 다 걸어야 한다』가 세상에 나온 지 꼬박 20년이 흐른 지금, 시인은 계속해서 앞으로 나아가며 ‘미래’에 대한 이야기를 꺼내놓는다.', '신용목 (지은이)', '문학과지성사', 'https://image.aladin.co.kr/product/34336/1/cover200/8932042977_1.jpg', '국내도서>소설/시/희곡>시>한국시', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('내세에는 남남이 좋겠어 8 - S코믹스, 초판한정 일러스트 카드', '수수께끼의 남자 아자미에게 유괴당했던 요시노. 키리시마의 도움으로 일이 더 커지진 않았지만, 키리시마는 큰 부상을 입고 만다. 아자미의 정체, 요시노 아버지 죽음의 진상, 수수께끼가 수수께끼를 부르던 중, 마침내 키리시마의 과거가 밝혀진다. 요시노에 대한 한결같은 마음의 시작이 여기에!', '코니시 아스카 (지은이), 박소현 (옮긴이)', '㈜소미미디어', 'https://image.aladin.co.kr/product/34313/42/cover200/k792932471_1.jpg', '국내도서>만화>순정만화>틴에이지 순정', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('고요의 바다에서', '발표하는 작품마다 베스트셀러에 오르며 오늘날 가장 주목받는 SF 작가로 자리매김한 에밀리 세인트존 맨델의 신작 장편소설. 우리에게 삶에서 진정 중요한 것은 무엇인지, 종말에 가까운 위기 앞에서 어떤 선택을 하며 살아갈 것인지를 차분하게, 또 묵직하게 묻는다.', '에밀리 세인트존 맨델 (지은이), 강동혁 (옮긴이)', '열린책들', 'https://image.aladin.co.kr/product/34312/24/cover200/8932924376_1.jpg', '국내도서>소설/시/희곡>과학소설(SF)>외국 과학소설', 1, 1);
