INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('놓지 마 과학! 19 - 라면 먼저? 수프 먼저?', '한국에서 137만 부, 일본에서 63만 부가 넘게 판매된 한일 베스트셀러, &lt;놓지 마 과학!&gt; 19권이 출간되었다. 이번 책은 오랜만에 신태훈, 나승훈 콤비의 유머 센스가 돋보이는 일상 단편 에피소드 구성으로 다시 돌아왔다.', '신태훈, 나승훈 (지은이), 홍훈기 (감수)', '위즈덤하우스', 'https://image.aladin.co.kr/product/34155/83/cover200/k032931590_1.jpg', '국내도서>어린이>과학/수학/컴퓨터>과학 일반', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('어떤 동사의 멸종 - 사라지는 직업들의 비망록', '첫 책 《퀴닝》(‘인간의 조건’ 개정판)으로 세간의 주목을 받고, 두 번째 책 《고기로 태어나서》로 제59회 한국출판문화상(교양 부문)을 수상한 작가 한승태가 ‘사라지는 직업들의 풍경’을 기록한 《어떤 동사의 멸종》을 펴냈다.', '한승태 (지은이)', '시대의창', 'https://image.aladin.co.kr/product/34129/31/cover200/895940845x_1.jpg', '국내도서>사회과학>비평/칼럼>한국사회비평/칼럼', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('쓰게 될 것', '2006년 등단 이후 《구의 증명》, 《해가 지는 곳으로》, 《단 한 사람》 등 극한의 상황에서도 사람과 사랑을 지키려 애쓰는 마음을 다채로운 소설적 상상으로 구현해온 작가 최진영 소설집. 여기에 실린 여덟 편의 소설은 2020년부터 2023년 사이에 쓰고 발표한 작품들로 기후 위기, 전쟁, AI, 여성 서사, 젊은 노인, 빈부 격차, 질병권 등 현재를 사는 우리가 내려놓지 않고 사유해야 할 문제에 정면으로 맞선 이야기들이다.', '최진영 (지은이)', '안온북스', 'https://image.aladin.co.kr/product/34127/18/cover200/k202931193_1.jpg', '국내도서>소설/시/희곡>한국소설>2000년대 이후 한국소설', 1, 1);
