INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('히든 픽처스', '“기이할 정도로 풍부한 상상력을 가진 작가(뉴욕타임스)”라는 평가를 듣는 제이슨 르쿨락의 신작 미스터리 스릴러 《히든 픽처스》가 문학수첩에서 출간되었다. 기이하고 충격적인 비밀을 간직한 어린 소년과 부모 그리고 보모로 일하는 젊은 여성을 둘러싼 초자연적인 스릴러로, 오싹하지만 아름답고 가슴 저릿한 미스터리가 독자를 사로잡는다.', '제이슨 르쿨락 (지은이), 유소영 (옮긴이)', '문학수첩', 'https://image.aladin.co.kr/product/33837/69/cover200/k582930998_1.jpg', '국내도서>소설/시/희곡>추리/미스터리소설>영미 추리/미스터리소설', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('모모 - 출간 50주년 기념 개정판', '환상 문학의 거장 미하엘 엔데가 남긴 최고의 걸작인 『모모』는 꿈처럼 펼쳐지는 신비로운 동화의 형식을 빌려 우리가 사는 세상을 예리하게 비춰 보여 주어 『어린 왕자』의 뒤를 잇는 어른을 위한 동화라는 수식도 따라붙는다. 동화적 향수를 자아내는 환상적인 모험 이야기에는 시간과 삶, 관계에 대한 깊이 있는 통찰이 가득 담겨 세대를 막론하고 어린이와 어른 들의 마음속에 깊은 울림을 준다.', '미하엘 엔데 (지은이), 한미희 (옮긴이)', '비룡소', 'https://image.aladin.co.kr/product/33712/5/cover200/8949139995_1.jpg', '국내도서>어린이>동화/명작/고전>세계명작', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('기억 전달자 - 30만 부 기념 개정판', '2007년 국내에 출간된 후 17년 동안 스테디셀러로 사랑받으며 청소년이 꼭 읽어야 할 현대 고전 SF로 자리 잡은 로이스 로리의 소설 『기억 전달자』가 국내 누적 판매 30만 부를 기념해 개정판으로 출간되었다.', '로이스 로리 (지은이), 장은수 (옮긴이)', '비룡소', 'https://image.aladin.co.kr/product/33710/34/cover200/894912355x_1.jpg', '국내도서>청소년>청소년 문학>청소년 소설', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('달터뷰', '달의 부드러움과 포근함을 전하고 독자의 마음을 어루만지는 그림책. 《달터뷰》를 쓰고 그린 임윤 작가는 달에게 하루 동안 있었던 일을 종종 털어놓다가 한 번쯤은 달의 이야기도 들어 보고 싶다고 생각했다. 그리고 달이 우리에게 이야기를 건네는 내용의 그림책을 완성했다.', '임윤 (지은이)', '풀빛', 'https://image.aladin.co.kr/product/32340/80/cover200/k152935360_1.jpg', '국내도서>어린이>초등1~2학년>그림책', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('아비투스 (양장) - 인간의 품격을 결정하는 7가지 자본', '2020년 출간 즉시 인문 분야 베스트셀러에 오르며 신드롬을 일으킨 『아비투스』가 양장특별판으로 새롭게 출간되었다. 이번 특별판은 한국어판 서문을 추가하고 고급스러운 하드커버로 소장 가치를 높였다.', '도리스 메르틴 (지은이), 배명자 (옮긴이)', '다산초당(다산북스)', 'https://image.aladin.co.kr/product/31396/12/cover200/k082832106_1.jpg', '국내도서>인문학>교양 인문학', 1, 1);
