INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('밥을 지어요', '이재명 시장 집의 삼시세끼 밥상 풍경을 담았다. 아내 김혜경은 한장한장 정성스레 작성한 레시피를 펼치듯 그녀만의 맛있는 이야기를 들려준다. 시어머니에게 전수받은 메뉴, 친정엄마에게 배운 요리, 남편의 보양을 위해 차린 메뉴 등 66품의 집밥 레시피를 담았다.', '김혜경 (지은이)', '김영사', 'https://image.aladin.co.kr/product/13212/69/cover200/8934980591_1.jpg', '국내도서>요리/살림>생활요리', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('삐뽀삐뽀 119 이유식 - 소아청소년과 전문의 하정훈이 전하는 건강한 아가 밥상', '2005년 초판 출간 이래 아기들의 이유식 정석을 이끌어온 《삐뽀삐뽀 119 이유식》전면개정판. 최신의 의학 정보를 바탕으로 과학적이고 체계적으로 이유식 하는 방식, 좀더 쉽고 재밌게 요리하여 초보 엄마아빠도 따라 할 수 있도록 도운 레시피, 좀더 보기 편한 디자인, 무엇보다 동영상을 보며 따라 할 수 있도록 링크를 걸어 새롭게 펴냈다.', '하정훈 (지은이)', '유니책방', 'https://image.aladin.co.kr/product/12485/33/cover200/s532930547_1.jpg', '국내도서>좋은부모>육아>성장/발달', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('노인과 바다', '노벨 문학상, 퓰리처상 수상 작가, 20세기 미국 문학을 개척한 작가 어니스트 헤밍웨이의 대표작. 미국 현대 문학의 개척자라 불리는 헤밍웨이는 제1차 세계대전 후 삶의 좌표를 잃어버린 ''길 잃은 세대''를 대표하는 작가이다. ''민음사 세계문학전집'' 278권으로 출간된 &lt;노인과 바다&gt;는 헤밍웨이의 마지막 소설로, 작가 고유의 소설 수법과 실존 철학이 짧은 분량 안에 집약되어 있다.', '어니스트 헤밍웨이 (지은이), 김욱동 (옮긴이)', '민음사', 'https://image.aladin.co.kr/product/1452/24/cover200/8937462788_3.jpg', '국내도서>소설/시/희곡>영미소설', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('월든 - 완결판', '19세기 미국의 위대한 저술가이자 사상가인 헨리 데이빗 소로우의 대표작 &lt;월든&gt;은 시간이 지날수록 그 가치를 더해가고 전 세계 독자들을 끊임없이 새로이 각성시키는 불멸의 고전이다. 그동안 국내에 수많은 번역본이 출간되었지만, 강승영 번역의 &lt;월든&gt; 2011년 개정판이 새롭게 출간되었다.', '헨리 데이비드 소로 (지은이), 강승영 (옮긴이)', '은행나무', 'https://image.aladin.co.kr/product/1284/8/cover200/8956605416_3.jpg', '국내도서>에세이>외국에세이', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('달과 6펜스', '<달과 6펜스>는 15종에 이르는 번역본이 이미 소개되어 있을 만큼 국내에서 크게 환영받는 작품이다. 이 작품은 서머싯 몸을 전세계에 널리 알린 결정적인 작품으로 제1차 세계대전이 끝난 이듬해인 1919년에 출판되어 대단한 인기를 끌었다.', '서머싯 몸 (지은이), 송무 (옮긴이)', '민음사', 'https://image.aladin.co.kr/product/23/77/cover200/s692639624_1.jpg', '국내도서>소설/시/희곡>영미소설', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('2024 김건호 헌법 최근 3개년 기출·예상 헌법판례 하반기', '국가직 7급, 지방직 7급, 변호사, 5급 공채, 입법고시, 소방간부, 경정승진, 법무사 등 각종 시험 대비 헌법 판례집이다. 2021년 1월부터 2024년 7월까지 최근 3개년 판례 중 이미 출제되었거나 출제가 유력한 판례들을 정리하여 수록했다.', '김건호 (지은이)', '메가스터디교육(공무원)', 'https://image.aladin.co.kr/product/34558/53/cover200/k102933166_1.jpg', '국내도서>수험서/자격증>공무원 수험서>7/9급 공무원>법학>헌법', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('섭리하심 - 내 삶 곳곳에 감추어진 하나님의 손길', '선한목자교회 후임 담임목사인 김다위 목사가 자신이 하나님과 동행하며 하나님을 향해 나아갔던 믿음의 여정 가운데, 자신과 자신의 가정과 목회 가운데 보이지 않는 하나님의 손길로 직조되고 있던 하나님의 섭리를 증거하고 있다.', '김다위 (지은이)', '규장(규장문화사)', 'https://image.aladin.co.kr/product/34483/77/cover200/k592932644_1.jpg', '국내도서>종교/역학>기독교(개신교)>기독교(개신교) 목회/신학>설교/성경연구', 1, 1);
