INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('백 살까지 유쾌하게 나이 드는 법 - 어차피 살 거라면, 개정증보판', '누적 50만 부 베스트셀러 작가 이근후 이화여대 명예교수의 화제작 《백 살까지 유쾌하게 나이 드는 법》이 출간 5주년을 맞아 개정증보판으로 재탄생했다. 90년 인생에서 길어 올린 나이 듦에 관한 철학적 통찰과 진솔한 조언을 담은 이 책은 해외 18개국에 판권이 수출되고, 영국 일간지 〈가디언〉에 인터뷰 기사가 전면 게재되고, 출간 즉시 인문 베스트셀러에 오르는 등 각종 화제를 낳았다.', '이근후 (지은이)', '메이븐', 'https://image.aladin.co.kr/product/34367/90/cover200/k702932006_1.jpg', '국내도서>인문학>교양 인문학', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('유골에 대한 기이한 취향', '인간사 최고 난제에 대한 깊이 있는 철학이 녹아 있는 역사추리소설의 클래식 ‘캐드펠 수사 시리즈(The Chronicles of Brother Cadfael)’가 원작의 시리즈 완간 30년을 기념해 한국어판 전면 개정으로 화려하게 옷을 갈아입고 독자들을 만난다.', '엘리스 피터스 (지은이), 최인석 (옮긴이)', '북하우스', 'https://image.aladin.co.kr/product/34324/68/cover200/k202932588_2.jpg', '국내도서>소설/시/희곡>추리/미스터리소설>영미 추리/미스터리소설', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('2025 유휘운 행정법총론 기출문제 풀어주는 기본서 (기.풀.기.)  - 전2권 - 9·7급 공무원, 소방, 군무원 시험 대비', '9.7급 공무원, 소방, 군무원 등 각종 공무원 시험 대비를 위한 행정법 기본서다. 행정법 이론과 기출 학습을 동시에 할 수 있도록 시중 기출문제집의 두 배 가까운 문제수(1,750제)를 담아내었고, 그것을 풀어주는 과정을 기본서에 녹여내었다.', '유휘운 (지은이)', '메가스터디교육(공무원)', 'https://image.aladin.co.kr/product/34313/11/cover200/k902932470_1.jpg', '국내도서>수험서/자격증>공무원 수험서>7/9급 공무원>행정법>7/9급 교재', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('가벼운 고백 - 김영민 단문집', '무겁기도 가볍기도 한 삶에서 완전한 희망에도 절망에도 치우치지 않고 절묘한 통찰을 끌어내는 우리 시대의 문장가, 서울대 김영민 교수의 아포리즘집. 2007년부터 2024년까지 무려 17년간 써내려간 문장을 선별해 엮은 단문 365편이 담겼다.', '김영민 (지은이)', '김영사', 'https://image.aladin.co.kr/product/34278/26/cover200/8934920890_1.jpg', '국내도서>에세이>한국에세이', 1, 1);
