INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('지적 대화를 위한 넓고 얕은 지식 : 제로 편', '《지적 대화를 위한 넓고 얕은 지식》 시리즈 제로편. 이원론 전에 훨씬 더 오랜 시간을 지배했던 일원론을 다룬 책이다. 인류 사상사에서 가장 중요한 지식으로 꼽히는 동양의 사상과 인물들을 흥미진진하게 소개한다.', '채사장 (지은이)', '웨일북', 'https://image.aladin.co.kr/product/21984/15/cover200/k622636607_3.jpg', '국내도서>인문학>교양 인문학', 1, 1);
