INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('자연의 가장자리와 자연사', '신해욱 시인의 다섯 번째 시집. 존재의 경계를 부드럽게 넘나드는 시 49편으로 언어와 세계, 그 가장자리를 깊이 탐구하고 성찰한다. 순정한 시인의 손끝에서 되살아난 말들의 경로와 역사를 살필 수 있다.', '신해욱 (지은이)', '봄날의책', 'https://image.aladin.co.kr/product/34426/70/cover200/k882932027_1.jpg', '국내도서>소설/시/희곡>시>한국시', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('게임 밸런스 수치 기획 바이블 - 유저를 붙잡아두는 수치 기획의 모든 것', '게임 기획에서 비중이 커지고 있는 수치 기획을 전문적으로 다루는 최초의 책이다. 준비 작업, 전투 수치, 경제 수치, 수치 검증, 수익화의 5단계 프로세스를 조망하는 것으로 시작해, 시각화를 통해 게임 수치에 따른 유저의 게임 경험을 알아본다.', '위안자오양 (지은이), 권유라 (옮긴이)', '제이펍', 'https://image.aladin.co.kr/product/34411/95/cover200/k442932022_1.jpg', '국내도서>컴퓨터/모바일>PC/게임/디지털 카메라>게임', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('격차 - 빈곤과 불평등의 세기를 끝내기 위한 탈성장의 정치경제학', '유엔 《인간개발보고서》 통계자문위원, 유럽 그린뉴딜 자문위원으로 활동하고 있는 신진 경제인류학자 제이슨 히켈의 대표 저작으로 제국주의부터 신자유주의에 이르기까지 잔혹한 모습으로 진화해온 빈곤과 불평등의 역사를 살펴보고 이를 극복하기 위한 현실적이고 대담한 해법을 제시하는 책이다.', '제이슨 히켈 (지은이), 김승진 (옮긴이), 홍기빈 (해제)', '아를', 'https://image.aladin.co.kr/product/34316/62/cover200/k712932476_1.jpg', '국내도서>사회과학>사회사상/사회사상사>자유주의/신자유주의', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('생각이 너무 많은 나에게 - 후회와 걱정에서 벗어나 지금을 살기 위한 심리학자의 마음 수행 가이드', '《미래의 나를 구하러 갑니다》, 《내 마음을 읽는 시간》 등으로 신경과학의 최근 발견들을 토대로 우리 삶에 구체적 실천을 위한 심리학 지식을 알려주는 변지영 작가의 ‘너무 많은 생각 때문에 피곤한 현대인을 위해 생각 감옥에서 탈출하는 법’을 안내하는 《생각이 너무 많은 나에게》가 출간되었다.', '변지영 (지은이)', '오아시스', 'https://image.aladin.co.kr/product/34312/13/cover200/k172932478_1.jpg', '국내도서>인문학>심리학/정신분석학>교양 심리학', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('홍춘욱의 최소한의 경제 토픽 - 달라진 세계를 이해하는 21세기 경제사 수업', '패러다임 시프트(paradigm shift), 대전환의 시대가 찾아왔다. 경제 원리를 꿰뚫은 자가 부를 쟁취하는 현재, 대한민국 대표 이코노미스트 홍춘욱이 21세기의 새로운 패러다임과 세계 경제 흐름을 한눈에 파악할 수 있도록 14가지 경제 토픽을 엄선해 신간 『홍춘욱의 최소한의 경제 토픽』에 집약했다.', '홍춘욱 (지은이)', '리더스북', 'https://image.aladin.co.kr/product/34296/59/cover200/8901285177_1.jpg', '국내도서>경제경영>경제학/경제일반>경제이야기', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('퀸의 대각선 1', '베르나르 베르베르가 신작 소설 『퀸의 대각선』으로 돌아왔다. 개인의 뛰어난 역량이 인류 진보의 원동력이라고 믿는 모니카와, 함께 뭉친 집단이 역사를 움직인다고 믿는 니콜, 두 여성이 국제 정치 무대에서 격돌하면서 벌어지는 일을 다룬 작품이다.', '베르나르 베르베르 (지은이), 전미연 (옮긴이)', '열린책들', 'https://image.aladin.co.kr/product/34121/41/cover200/8932924392_1.jpg', '국내도서>소설/시/희곡>프랑스소설', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('어느 하루 눈부시지 않은 날이 없었습니다 - 대중문화평론가 정덕현의 가슴에 오랫동안 남은 명대사들', '힘든 하루 끝에 나와 같이 웃어주고 울어주는 드라마 한 편이 있다면 인생이 조금은 살만하다는 생각이 든다. 오늘 하루만큼은 해피엔딩을 만들고 싶은 사람들에게 이 책을 권한다. 지친 삶에 위로가 되는 드라마 속 대사 한마디를 발견할 수 있을 것이다.', '정덕현 (지은이)', '페이지2(page2)', 'https://image.aladin.co.kr/product/34108/59/cover200/k812931285_1.jpg', '국내도서>에세이>한국에세이', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('원칙으로 수익 내는 단타의 기술 - 상위 1% 주식 트레이더가 지킨 단 하나의 투자 비법', '어떻게 확률 높은 자리를 찾을 수 있었는지, 각 매매법을 활용할 때 매수와 매도의 기준이 무엇인지, 어떻게 해야 흔들리지 않고 칼 손절을 할 수 있는지, 시장에 따른 매매법 등 그가 주식 트레이딩을 하며 쌓아온 경험치들을 하나도 빠짐없이 기록했다.', '원정연(만쥬) (지은이)', '길벗', 'https://image.aladin.co.kr/product/33967/98/cover200/k272930347_1.jpg', '국내도서>경제경영>재테크/투자>주식/펀드', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('누군가를 이토록 사랑한 적', '시인 이병률의 일곱번째 시집 『누군가를 이토록 사랑한 적』이 문학과지성사 시인선 601번으로 출간되었다. 사랑이라는 명명하에 바닷빛과 하늘빛이 절묘하게 어우러진 테두리와 낮은 채도의 소라색 바탕이 겹쳐진 이번 시집은 마치 파블로 피카소가 절친한 친구의 자살 이후 짙은 푸른색만을 고집했던 청색시대(1901~1904)를 연상시킨다.', '이병률 (지은이)', '문학과지성사', 'https://image.aladin.co.kr/product/33824/43/cover200/8932042721_1.jpg', '국내도서>소설/시/희곡>시>한국시', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('게으른 자를 위한 수상한 화학책 - 지식 쌓고 시간 버는 기적의 화학 수업', '주기율표를 외우는 것이 화학의 전부라고 오해하는 과알못(과학을 잘 알지 못하는)을 위해, 일상에서 화학의 즐거움을 발견하는 경험을 선물하기 위해 이광렬 교수가 나섰다. 누구나 해야 하지만 시간 도둑의 주범인, 게다가 화학과 밀접한 연관이 있는 일상의 집안일에 주목했다.', '이광렬 (지은이)', '블랙피쉬', 'https://image.aladin.co.kr/product/33664/64/cover200/8968334498_1.jpg', '국내도서>과학>화학>쉽게 배우는 화학', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('물질의 세계 - 6가지 물질이 그려내는 인류 문명의 대서사시', '저자이자 영국의 저널리스트 에드 콘웨이는 우리가 알지 못했고 볼 수 없었던 물질이 가진 경이로운 세계로 독자들을 안내한다. 무더운 유럽의 가장 깊은 광산부터 티끌 하나 없는 대만의 반도체 공장, 칠레 아타카마 사막의 소금호수까지. 전 세계 곳곳을 탐험하는 과정 속에 인간의 새로운 미래를 가져다 줄 대체 불가능한 여섯 가지 물질의 비밀이 밝혀진다.', '에드 콘웨이 (지은이), 이종인 (옮긴이)', '인플루엔셜(주)', 'https://image.aladin.co.kr/product/33478/66/cover200/k992938150_2.jpg', '국내도서>인문학>교양 인문학', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('말하고 싶은 비밀', '고등학교 2학년에 재학 중이며 방송부원으로 활동하는 여고생 구로다 노조미에게 어느 날 날아든 짤막한 러브레터. 보낸 사람은 학교 최고 인기 남 세토야마 준이다. 답장으로 에둘러 거절했지만, 친구부터 시작하자는 당찬 고백에 흔들리게 된 구로다는 세토야마와 쪽지를 주고받기 시작한다.', '사쿠라 이이요 (지은이), 김윤경 (옮긴이)', '모모', 'https://image.aladin.co.kr/product/32966/94/cover200/k432936257_1.jpg', '국내도서>소설/시/희곡>로맨스소설>외국 로맨스소설', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('돈, 뜨겁게 사랑하고 차갑게 다루어라', '앙드레 코스톨라니 최후의 역작으로 증권 거래와 투자심리에 중요한 변수인 투자의 근본적인 비밀과 기술 등을 안내한 돈 관리 지침서다. 저자는 투자자들에게 21세기 증권시장에 있어서 기회와 위험, 그리고 변화와 상승, 하락에 대한 전망을 보여준다.', '앙드레 코스톨라니 (지은이), 한윤진 (옮긴이)', '미래의창', 'https://image.aladin.co.kr/product/32489/85/cover200/k942935503_1.jpg', '국내도서>경제경영>재테크/투자>주식/펀드', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('JLPT 일본어능력시험 한권으로 끝내기 N5 - 2022 최신 개정판', '2010년부터 2021년까지 실제 시험에 출제된 어휘와 문법이 실려 있으며 그에 맞는 문제 유형과 문제로 구성된 JLPT(일본어능력시험) N5 수험서다. 네 가지 시험 영역을 모두 다루고 있어 종합적으로 JLPT N5를 마스터할 수 있다.', '이치우, 이한나 (지은이)', '다락원', 'https://image.aladin.co.kr/product/30070/53/cover200/8927712455_3.jpg', '국내도서>외국어>일본어>JLPT>N5', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('애니캔', '별숲 동화 마을 41권. 동면 기술과 특별한 먹이를 이용해 어린 반려동물을 알루미늄 캔에 담아 판매한다는 독특하고 흥미로운 설정으로 동물권 존중의 메시지를 담은 은경 동화작가의 SF 장편동화이다.', '은경 (지은이), 유시연 (그림)', '별숲', 'https://image.aladin.co.kr/product/28978/99/cover200/k122836924_1.jpg', '국내도서>어린이>동화/명작/고전>국내창작동화', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('삼체 1부 : 삼체문제', '아시아 최초로 휴고상을 수상하며 SF 거장으로 등극한 류츠신의 대표작. 『1부―삼체문제』 『2부―암흑의 숲』 『3부―사신의 영생』으로 이어지는 ‘지구의 과거’ 3부작은 무수한 위험이 숨어 있는 ‘암흑의 숲’과 같은 우주에서 인류가 마주하게 될 운명을 대담한 상상력으로 그려냈다.', '류츠신 (지은이), 이현아 (옮긴이)', '자음과모음', 'https://image.aladin.co.kr/product/28912/65/cover200/s772939656_2.jpg', '국내도서>소설/시/희곡>과학소설(SF)>외국 과학소설', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('완전한 행복', '버스도 다니지 않는 버려진 시골집에서 늪에 사는 오리들을 먹이기 위해 오리 먹이를 만드는 한 여자의 뒷모습에서 시작된다. 그녀와 딸, 그리고 그 집을 찾은 한 남자의 얼굴을 비춘다. 얼굴을 맞대고 웃고 있지만 그들이 추구하는 서로 다른 행복은 서서히 불협화음을 만들어낸다.', '정유정 (지은이)', '은행나무', 'https://image.aladin.co.kr/product/27225/29/cover200/k432732398_3.jpg', '국내도서>소설/시/희곡>한국소설>2000년대 이후 한국소설', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('봄눈', '일본 문학 애호가들 사이에서 전설로 회자되는 소설, 미시마 유키오의 ''풍요의 바다'' 시리즈 첫 번째 권. 작가는 이 시리즈에서 환생을 거듭하는 한 영혼과 그를 추적하는 인식자의 궤적을 통해 20세기 일본의 파노라마를 펼쳐 냈다.', '미시마 유키오 (지은이), 윤상인, 손혜경 (옮긴이)', '민음사', 'https://image.aladin.co.kr/product/25127/81/cover200/8937479834_1.jpg', '국내도서>소설/시/희곡>일본소설>1950년대 이후 일본소설', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('종의 기원', '펴내는 작품마다 압도적인 서사와 폭발적인 이야기의 힘으로 많은 독자들의 뜨거운 사랑을 받아온 정유정이 전작 &lt;28&gt; 이후 3년 만에 장편소설 &lt;종의 기원&gt;으로 독자들을 찾았다. 작품 안에서 늘 허를 찌르는 반전을 선사했던 작가답게, 이번 작품에서 정유정의 상상력은 전혀 다른 방향에서 빛을 발한다.', '정유정 (지은이)', '은행나무', 'https://image.aladin.co.kr/product/7492/9/cover200/8956609950_2.jpg', '국내도서>소설/시/희곡>한국소설>2000년대 이후 한국소설', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('동물농장', '1945년에 간행된 조지 오웰의 대표작. 어떤 농장의 동물들이 늙은 돼지 메이저의 부추김에 빠져 농장주의 압제에 대항해 반란을 일으켜 인간의 착취가 없는 ''모든 동물이 평등한 이상사회''를 건설한다. 그러나 돼지들이 지도자가 되어 다시 동물들을 압박하기 시작하는데...', '조지 오웰 (지은이), 도정일 (옮긴이)', '민음사', 'https://image.aladin.co.kr/product/4/6/cover200/s93746005x_3.jpg', '국내도서>소설/시/희곡>영미소설', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('달리는 강하다', '《재와 물거품》 《해저도서 타코야키》 등의 베스트셀러로 사랑받아온 김청귤 작가가 첫 청소년소설 《달리는 강하다》로 찾아왔다. 망가진 세상 속에서도 서로 보듬으며 성장하고, 행복을 찾는 따뜻한 서사를 선보여온 김청귤은 이번 책에서 자신의 이러한 특장이 청소년소설에서 더욱 빛을 발할 수 있음을 증명해 보인다.', '김청귤 (지은이)', '래빗홀', 'https://image.aladin.co.kr/product/34552/69/cover200/k802933065_1.jpg', '국내도서>청소년>청소년 문학>청소년 소설', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('대국 - 기본에서 최선으로', '2005년 홀로 5연승을 하며 제6회 농심신라면배의 우승컵을 들어 올린 이창호 9단의 ‘상하이 대첩’을 넘어 ‘상하이 신화’를 창조한 신진서 9단의 끝내기 6연승, 그 긴박하고도 위대한 여정이 『대국 : 기본에서 최선으로』에서 펼쳐진다.', '신진서 (지은이)', '휴먼큐브', 'https://image.aladin.co.kr/product/34529/83/cover200/k392932951_1.jpg', '국내도서>에세이>한국에세이', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('데스러버 4 - S코믹스', '갑작스럽게 미소녀들만 있는 감옥에 납치, 감금된 고등학생 후지시로 코스케. 그녀들의 목적은 모인 동정들의 정조. 그 이유도 모르는 와중에 단 한 가지 알고 있는 것은 ‘해버리면 살해당한다’는 사실. 동정의 자존심을 걸고 위기를 넘어섰지만, ‘죄수의 딜레마’ 작전으로 인해 기어코 한 명이 함락되는데…….', '토모히로 카이 (지은이), 카즈타카 (원작), 천선필 (옮긴이)', '㈜소미미디어', 'https://image.aladin.co.kr/product/34483/39/cover200/k322932642_1.jpg', '국내도서>만화>순정만화>레이디스 코믹', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('와인의 시간 - 애호가와 입문자, 모두를 위한 최고의 교양 수업', '와인이라는 여행에 꼭 필요한 기본 정보를 소개하는 것에서 출발한다. 와인의 스타일과 포도 품종, 테루아, 양조법, 맛 표현법, 보관법, 음식 페어링 등 자칫 길을 잃기 쉬울 정도로 방대한 정보의 홍수 속에서 필수 지식만 엄선했다.', '김욱성 (지은이)', '은행나무', 'https://image.aladin.co.kr/product/34474/32/cover200/k752932632_1.jpg', '국내도서>요리/살림>술/음료/차>술', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('리스키 슈거 스파이스 - 뉴 루비코믹스 3023', '', 'JITA (지은이)', '현대지능개발사', 'https://image.aladin.co.kr/product/34469/71/cover200/k752932533_1.jpg', '국내도서>만화>순정만화>그 남자들의 사랑', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('쏘쿨의 인서울 인강남 내집마련', '2022년 10월, 모두가 부동산 하락을 말할 때 서울 폭등! 강남 폭등!을 외치며 복귀한 쏘쿨. 그는 15년 전 ‘아파트 시세지도’를 창시하고 내집마련 투자 방식을 송두리째 바꾼 장본인이다. 입지, 평형, 시세 비교를 직접 그려서 한눈에 파악하는 내집마련 여행지도. 그 당시 쏘쿨에게 배운 수강생들이 지금은 네임드 부동산 강사가 되었다. 부동산 멘토들의 멘토! 24년차 부동산 전문가 쏘쿨이 2번째 책으로 돌아왔다.', '쏘쿨 (지은이)', '진서원', 'https://image.aladin.co.kr/product/34425/84/cover200/k192932026_2.jpg', '국내도서>경제경영>재테크/투자>부동산/경매', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('자연스러운 포즈 드로잉 - 360° 모든 방향에서 그리는 인체의 도형화 그리고 해부학까지', '『자연스러운 인체 드로잉』으로 인체의 기본기를 다졌다면 인체의 움직임에 대해서 연구하고 적용해 볼 단계이다. 인체를 그린다는 것은 궁극적으로 움직이는 순간을 포착한다는 것이기 때문이다. 다양한 포즈를 자유자재로 그리려면 인체를 참고 자료 없이도 360° 시뮬레이션 할 수 있어야 한다. 그러기 위해서는 우선 기본기가 탄탄해야 하고, 이 기본기를 바탕으로 직접 포즈를 360° 방향으로 그려보는 연습을 통해 데이터와 지식을 쌓아야 한다.', '소은 박경선 (지은이)', '동양북스(동양문고)', 'https://image.aladin.co.kr/product/34349/57/cover200/k572932990_1.jpg', '국내도서>예술/대중문화>미술>미술 실기', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('겪어보면 안다 - 김홍신의 인생 수업', '짧지만 큰 울림을 주는 이 글은 베스트셀러 『인간시장』 『인생사용설명서』의 작가로 수많은 독자에게 사랑받아 온 소설가 김홍신이 인생을 살아가며 수없이 경험하고 깨달은 삶의 소회다.', '김홍신 (지은이)', '해냄', 'https://image.aladin.co.kr/product/34286/42/cover200/k872932077_1.jpg', '국내도서>에세이>한국에세이', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('못해 그리고 안 할 거야 - 리디아 데이비스 이야기집', '리디아 데이비스는 시인지 에세이인지 소설인지 구분이 모호한 글을 쓰는데, 그 자신은 그저 ‘이야기(stories)’로 불러주기를 바란다. 《못해 그리고 안 할 거야》는 독특한 형식의 글쓰기를 시도하며 자신만의 문학적 반경을 넓혀온 작가 리디아 데이비스의 진면모를 확인할 수 있는 이야기집이다.', '리디아 데이비스 (지은이), 이주혜 (옮긴이)', '에트르', 'https://image.aladin.co.kr/product/34273/30/cover200/k992932066_1.jpg', '국내도서>소설/시/희곡>영미소설', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('공부는 멘탈 게임이다 - 나 혼자 레벨 업하는 전교 1등 의대생의 공부 비법', '전교 1등 출신 의대생이자 전국의 수많은 학생들과 학부모들의 공부 멘토로 활약 중인 저자가 510만 학생들에게 ''내신''도 ''수능''도 ''나 혼자 레벨 업''할 수 있는 공부 비법과 멘탈 관리 원칙을 담은 책이다.', '임민찬 (지은이)', '데이스타', 'https://image.aladin.co.kr/product/34213/76/cover200/k922931032_1.jpg', '국내도서>청소년>학습법', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('감정 호텔 - 내 마음이 머무는 곳', '끊임없이 밀려왔다 밀려가는 감정을 내 마음이라는 ‘호텔’을 찾아오는 손님에 빗대어 이야기를 풀어 간다. 작가 리디아 브란코비치는 자기 안에서 휘몰아치는 감정을 깊숙이 들여다보면서 떠오르는 생각을 차곡차곡 쌓아 이야기를 만들었다고 한다.', '리디아 브란코비치 (지은이), 장미란 (옮긴이)', '책읽는곰', 'https://image.aladin.co.kr/product/33487/4/cover200/k692938256_1.jpg', '국내도서>유아>그림책>나라별 그림책>유럽', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('푸른 사자 와니니 6 - 수사자 아산테', '‘푸른 사자 와니니’ 시리즈가 여섯 번째 이야기로 돌아왔다. 6권은 와니니 무리에서 태어난 첫 번째 수사자 ‘아산테’가 가족을 떠나 자기만의 길을 나서며 시작된다. 아산테는 수차례 위기를 극복하고 지혜로운 암사자들을 만나며 책임감 있는 수사자로 성장한다.', '이현 (지은이), 오윤화 (그림)', '창비', 'https://image.aladin.co.kr/product/32334/4/cover200/8936443313_1.jpg', '국내도서>어린이>동화/명작/고전>국내창작동화', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('아주 희미한 빛으로도', '‘함께 성장해나가는 우리 세대의 소설가’를 갖는 드문 경험을 선사하며 동료 작가와 평론가, 독자 모두에게 특별한 이름으로 자리매김한 최은영의 세번째 소설집. 이번 소설집은 이전 작품들에 담긴 문제의식을 한층 더 깊고 날카로운 시선으로 이어나간다.', '최은영 (지은이)', '문학동네', 'https://image.aladin.co.kr/product/32129/40/cover200/s672937436_1.jpg', '국내도서>소설/시/희곡>한국소설>2000년대 이후 한국소설', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('사카모토 데이즈 4 - 객차 내애서는 정숙히', '랩 지하 깊은 곳에서 사카모토 상점, 킬러집단, 그리고 ORDER 세 세력이 집결! 배후에 꿈틀거리는 ''그 남자''의 존재를 둘러싸고 긴박한 분위기가 형성된 가운데, 루를 탈환하기 위해 나선 사카모토와 신을 카시마와 세바가 덮치는데! 과연 구출작전의 향방은···?!', '스즈키 유우토 (지은이)', '대원씨아이(만화)', 'https://image.aladin.co.kr/product/29467/84/cover200/k392837339_1.jpg', '국내도서>만화>코믹/명랑만화', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('사랑의 기술', '프롬은 가장 능동적으로 자신의 퍼스낼리티 전체를 발달시켜 생산적 방향으로 나아가지 않는 한, 아무리 사랑하려고 노력해도 반드시 실패하기 마련이며, 이웃을 사랑하는 능력이 없는 한, 또한 참된 겸손, 용기, 신념, 훈련이 없는 한 개인적인 사랑도 성공할 수 없다고 말한다.', '에리히 프롬 (지은이), 황문수 (옮긴이)', '문예출판사', 'https://image.aladin.co.kr/product/20329/93/cover200/8931011628_1.jpg', '국내도서>인문학>인문 에세이', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('나의 눈부신 친구', '이탈리아 나폴리 폐허에서도 빛나는 두 여자의 우정을 담은 엘레나 페란테의 소설 ''나폴리 4부작''. 제1권 &lt;나의 눈부신 친구&gt;는 릴라와 레누라는 두 주인공의 유년기부터 사춘기까지의 우정에 초점을 맞추고 있다.', '엘레나 페란테 (지은이), 김지우 (옮긴이)', '한길사', 'https://image.aladin.co.kr/product/8695/78/cover200/s312932407_1.jpg', '국내도서>소설/시/희곡>세계의 소설>이탈리아소설', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('러브 누아르', '‘달달북다’의 세 번째 작품은 한정현의 『러브 누아르』이다. 역사와 문학의 경계에서 탐구적 태도를 가지고 윤리적 질문을 해온 작가는 데뷔 이후 적극적이고 활발한 창작 활동을 해왔다. 역사의 줄기를 따라 이어지는 한정현의 소설은 독자가 몰랐던 타인의 진실을 향한 슬픔과 애도의 방식에 다가갈 수 있게 한다.', '한정현 (지은이)', '북다', 'https://image.aladin.co.kr/product/34552/65/cover200/k672933065_1.jpg', '국내도서>소설/시/희곡>로맨스소설>한국 로맨스소설', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('오케이 다시 한번 해볼게요', '착하게 바르게 예쁘게 살고 싶다고 자신을 소개하는 박여름 작가의 신간이다. 전작인 &lt;좋은 일이 오려고 그러나 보다&gt;는 10만 부 이상 판매되는 쾌거를 이뤘다. 출판 시장은 점점 축소되고 그 안에서 에세이의 입지는 점점 더 줄어들고 있다. 그런 상황에서 전작이 좋은 성과를 이룬 건 여전히 많은 사람이 자신의 삶을 지탱해 줄 한 문장을 기다리고 있을지도 모른다는 뜻이 된다.', '박여름 (지은이)', '히읏', 'https://image.aladin.co.kr/product/34499/16/cover200/k652932750_1.jpg', '국내도서>에세이>한국에세이', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('삶이 허기질 때 나는 교양을 읽는다 3 - 하루 5분 감각이 업그레이드되는 최고의 인문학 만찬', '그 어디에서도 볼 수 없던 신선한 주제와 세련되고 탁월한 스토리텔링으로 누적 1억 뷰를 돌파하며 지식이 쌓이는 즐거움을 느끼게 해주는 국내 최고의 지식 채널 ‘지식 브런치’의 세 번째 책이다. 하루 5분, 강력한 흡입력과 뛰어난 몰입감으로 단숨에 지적 허기가 채워지는 동시에 세상을 보는 감각이 업그레이드되는 놀라운 효과를 경험할 수 있을 것이다.', '지식 브런치 (지은이)', '서스테인', 'https://image.aladin.co.kr/product/34443/96/cover200/k022932234_1.jpg', '국내도서>인문학>교양 인문학', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('변호사 어벤저스 2 - 동물 보호법, 책임감을 가져라!', '&lt;변호사 어벤저스&gt;는 출간 이후 어린이들의 꾸준한 사랑을 받고 있는 &lt;의사 어벤저스(전20권)&gt;의 확장 시리즈로, 시장 흐름과 독자들의 강력한 요구를 반영하여 기획 개발되었다. &lt;변호사 어벤저스&gt;는 우리나라 최초 ‘어린이 변호사 양성 프로젝트’를 거친, 수습이지만 어른 못지않은 열정을 가진 어린이 변호사들에 대한 이야기를 다룬 법학 동화이다.', '고희정 (지은이), 최미란 (그림), 신주영 (감수)', '가나출판사', 'https://image.aladin.co.kr/product/34411/44/cover200/k662932021_1.jpg', '국내도서>어린이>사회/역사/철학>정치/경제/법', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('갑절의 영감을 주옵소서 - 세상에서 하나님의 자녀로 살기 위한 부르짖음', '저자는 엘리야 선지자가 세상을 떠나기 직전에 “당신의 영감이 갑절이나 내게 있기를 구하나이다”(왕하 2:9, 개역한글)라고 간구한 엘리사 선지자의 부르짖음에 주목했으며, 혼란 가득한 세상에서 승리하기 위해선 성령의 능력이 필요함을 강조하며, 능력 있는 믿음의 삶을 위한 구체적인 대안들을 제시하고 있다.', '이찬수 (지은이)', '규장(규장문화사)', 'https://image.aladin.co.kr/product/34398/77/cover200/k672932515_3.jpg', '국내도서>종교/역학>기독교(개신교)>기독교(개신교) 신앙생활>신앙생활일반', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('비폭력 대화 - 일상에서 쓰는 평화와 공감의 언어', '2004년 가을 처음 출간된 이래 스테디셀러로 자리 잡은 『비폭력대화 : 일상에서 쓰는 평화의 언어, 삶의 언어』의 개정 2판이다. 독자들이 보다 쉽고 편하게 읽을 수 있도록 문장과 내용을 다듬고 수정했으며 느낌을 표현하는 어휘 목록 등을 보완해 실용성과 완성도를 높였다.', '마셜 B. 로젠버그 (지은이), 캐서린 한 (옮긴이)', '한국NVC출판사', 'https://image.aladin.co.kr/product/34363/53/cover200/k442932009_2.jpg', '국내도서>인문학>심리학/정신분석학>심리치료', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('호크니와 게이퍼드가 말하는 그림의 역사 - 동굴벽화부터 아이패드까지', '데이비드 호크니와 그의 오랜 친구이자 미술 평론가인 마틴 게이퍼드가 말하는 『그림의 역사』 개정 신판이 미술문화에서 출간되었다. 독자들의 편의를 위해 보다 가볍고 소지하기 용이한 크기의 판형을 도입하였다.', '데이비드 호크니, 마틴 게이퍼드 (지은이), 주은정 (옮긴이)', '미술문화', 'https://image.aladin.co.kr/product/34360/63/cover200/k852932909_2.jpg', '국내도서>예술/대중문화>미술>미술사', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('사카모토 데이즈 16 - 해후', '킬러전 티켓을 얻기 위해 분주히 움직인느 사카모토 일행. 옥션 암거래에 희망을 걸어보지만, 집요하게 방해하는 수수께끼의 인물과 쟁탈전이 벌어지는데?!', '스즈키 유우토 (지은이)', '대원씨아이(만화)', 'https://image.aladin.co.kr/product/34172/61/cover200/k852931118_1.jpg', '국내도서>만화>코믹/명랑만화', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('108요괴의 수염 - 김동식 주니어소설', '묘하게 빠져드는 여섯 이야기, 여섯 질문, 여섯 반전이 과감한 매력을 품고 있는 작품이다. 십 대들의 소소한 일상을 파고드는 기괴하고 이상하며 신비로운 상상력이 기묘한 판타지의 세계를 열어 보인다.', '김동식 (지은이), 조성흠 (그림)', '(주)학교도서관저널', 'https://image.aladin.co.kr/product/34158/37/cover200/8969151699_1.jpg', '국내도서>어린이>동화/명작/고전>국내창작동화', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('디즈니.픽사 인사이드 아웃 2', '라일리의 머릿속에는 핵심 감정인, 기쁨이, 슬픔이, 까칠이, 버럭이, 소심이가 있다. 어느 날, 사춘기 알람이 시끄럽게 울리기 시작하고, 새로운 감정인 불안이, 당황이, 부럽이, 따분이가 들어선다. 고등학교 입학이라는 변화를 앞두고 일어날 수 있는 최악의 상황을 1~100까지 모두 예상하고 대비하려는 불안이!', '디즈니 동화 아트팀 (그림), 백정선 (옮긴이)', 'Oh!북스', 'https://image.aladin.co.kr/product/34122/0/cover200/k272931090_1.jpg', '국내도서>유아>그림책>유아 캐릭터 그림책', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('2025 신광은 형법 총론 기본서', '체계적이고 정확한 형법 공부를 위한 최고의 기본서다. 2024년 1차 전 직렬 최신 기출문제, 판례를 모두 반영하였고, 이론/학설/조문/판례 최신 경향을 완벽 반영하였다.', '신광은 (지은이)', '느루(미래인재컴퍼니)', 'https://image.aladin.co.kr/product/33877/38/cover200/k812930603_1.jpg', '국내도서>수험서/자격증>공무원 수험서>경찰공무원(승진)>형법', 1, 1);
