INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('나는 배당투자로 매일 스타벅스 커피를 공짜로 마신다 - 평생 월 500만 원씩 버는 30일 기적의 배당 파이프라인 공략집', '트렌디한 분석과 진정성 있는 콘텐츠로 수십만 투자자들에게 사랑받는 유튜브 채널 ‘수페TV’의 운영자 송민섭이, 경제적 자유를 이루고 싶은 사람들과 부의 파이프라인을 마련하고 싶은 사람들을 위해 30일 만에 마스터하는 배당 투자 실전 노하우를 전한다.', '송민섭(수페TV) (지은이)', '21세기북스', 'https://image.aladin.co.kr/product/32956/53/cover200/k482936243_1.jpg', '국내도서>경제경영>재테크/투자>재테크/투자 일반', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('NEW 미꽃체 손글씨 노트 - 손글씨를 인쇄된 폰트처럼, 개정증보판', '지난 2년간 미꽃체가 많은 사랑을 받으면서 미꽃 작가의 글씨도 더욱 정교해졌다. 하루도 빠짐없이 글씨를 쓴 작가의 손끝에서 미꽃체도 자연스럽게 변화하고 성장한 것이다. 작가는 미꽃체를 사랑하고 배우고 싶은 사람들을 위해 기존 책에 새로운 미꽃체를 담은 《NEW 미꽃체 손글씨 노트》를 펴냈다.', '최현미 (지은이)', '시원북스', 'https://image.aladin.co.kr/product/31955/95/cover200/k362833256_3.jpg', '국내도서>건강/취미>글씨 쓰기', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('챗GPT와 기획.분석.보고 - 일주일 치 업무를 하루 만에 해치우는 일잘러의 ChatGPT 완벽 활용법', 'ChatGPT로 문제를 파악하고 시장을 조사해서 정리할 수 있다. 신제품 아이디어를 도출하고 전략을 수립할 수 있다. 또한 데이터를 분석하고 통찰과 시사점을 얻을 수 있으며, 실적과 성과를 분석하고 목표를 관리할 수 있다.', '김철수 (지은이)', '위키북스', 'https://image.aladin.co.kr/product/31479/46/cover200/k822832523_1.jpg', '국내도서>컴퓨터/모바일>인공지능', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('고요한 우연 - 제13회 문학동네청소년문학상 대상 수상작', '제13회 문학동네청소년문학상 대상 수상작. 지극히 평범한 이들이 지닌 작고도 반짝이는 힘을 그린 소설이다. “애쓰고 고뇌하며 작은 보폭으로나마 앞으로 나아가는 모습이 현재를 살아가는 청소년 인물의 진정한 성취를 보여 준다”는 평과 함께 수상의 영예를 안게 되었다.', '김수빈 (지은이)', '문학동네', 'https://image.aladin.co.kr/product/31113/80/cover200/s002831046_1.jpg', '국내도서>청소년>청소년 문학>청소년 소설', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('오늘 밤, 세계에서 이 사랑이 사라진다 해도', '제26회 전격소설대상 ‘미디어워크스문고상’ 수상작. 밤에 자고 일어나면 기억이 리셋되는 ‘선행성 기억상실증’을 앓는 소녀 히노 마오리와 무미건조한 인생을 살고 있는 평범한 고등학생 가미야 도루의 풋풋하고 애틋한 사랑 이야기를 담았다.', '이치조 미사키 (지은이), 권영주 (옮긴이)', '모모', 'https://image.aladin.co.kr/product/27407/79/cover200/k362732558_3.jpg', '국내도서>소설/시/희곡>일본소설>1950년대 이후 일본소설', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('결국 해내는 사람들의 원칙 (리커버 에디션) - 최신 뇌과학이 밝혀낸 성공의 비밀', '망상활성계를 활용해 어떻게 성공에 이를 수 있는지 그 방법을 차근차근 풀어 간다. 가장 먼저 자신의 목표를 명확하게, 그리고 눈으로 볼 수 있게 시각화해 설정하는 것이 중요하다. 그다음 내 목표에 대해 누가 뭐라 하든 밀고 나가는 힘이 있어야 한다.', '앨런 피즈, 바바라 피즈 (지은이), 이재경 (옮긴이)', '반니', 'https://image.aladin.co.kr/product/25810/88/cover200/k662736333_1.jpg', '국내도서>자기계발>성공>성공학', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('떡이 최고야 - 2017 전국학교도서관사서협회 추천,  2017 오픈키드 좋은 그림책 추천, 전국학교도서관사서협회 선정', '각각의 떡이 어떤 유래와 의미를 품고 있는지에 따라, 크게 명절떡과 잔치떡, 두 편으로 나누어 줄다리기를 하는 내용이다. 줄다리기에 앞서 의인화된 떡들이 어떤 날을 대표하는지 소개함으로써, 명절이나 잔치에 각각 어떤 떡을 먹는지 자연스럽게 알 수 있다.', '김난지 (글), 최나미 (그림)', '천개의바람', 'https://image.aladin.co.kr/product/8948/64/cover200/k672535699_1.jpg', '국내도서>유아>그림책>나라별 그림책>한국 그림책', 1, 1);
