INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('지구 끝 날의 요리사', '『창문 넘어 도망친 100세 노인』의 저자 요나스 요나손의 신작 장편소설. 늘 &lt;멍청이&gt;라 불리며 구박받던 요한은 어느 날 형 프레드리크가 남긴 캠핑카를  몰다가 종말 예언가 페트라의 캐러밴을 쳐서 굴러 떨어지게 만든다. 종말을 12일 앞두고 페트라가 목숨을 끊으려던 바로 그 순간이었다. 멍청하기는 하지만 요리는 끝내주게 잘하는 요한은 페트라에게 맛있는 음식과 술을 건넨다.', '요나스 요나손 (지은이), 임호경 (옮긴이)', '열린책들', 'https://image.aladin.co.kr/product/34469/26/cover200/8932924554_1.jpg', '국내도서>소설/시/희곡>세계의 소설>북유럽소설', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('소나기', '50,000 독자가 선택한 피도크 작가의 첫 번째 그림책. 소나기가 내린다는 소식에 아이의 발걸음이 분주해진다. 갑작스러운 비에 옷이 젖고, 감기에 걸리기 전에 우산을 찾아야 하기 때문인데….', '피도크 (지은이)', '포레스트북스', 'https://image.aladin.co.kr/product/34468/97/cover200/k762932530_1.jpg', '국내도서>유아>그림책>나라별 그림책>한국 그림책', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('네이티브처럼 듣고 말하는 영어 구동사 수업 - 구동사를 완벽하게 익히는 8단계 영어 학습법', '원어민 회화 패턴을 분석해 가장 많이 사용되는 구동사를 선별하여 네이티브처럼 배우고 사용할 수 있도록 구성되었다. 기존에는 영어 학습자가 구동사를 제대로 체화할 수 있는 책이 없었으나 이 책은 그런 필요를 완벽하게 충족시켜줄 것이다.', '조찬웅, Kayla Mundstock (지은이)', '동양북스(동양문고)', 'https://image.aladin.co.kr/product/34468/96/cover200/k552932530_1.jpg', '국내도서>외국어>영문법', 1, 1);
