INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('TIPS! 그림이 그리고 싶어지는 힌트 모음집', '작화 전문가가 알려주는 퍼스를 사용하지 않아도 그리는 배경, 아이레벨 접근 방법, 실수하기 쉬운 퍼스, 묘사의 밀도와 원근감, 색을 정하는 방법, 주변 사물을 그리는 방법 등등 요시다 세이지의 잘 그리는 노하우를 담은 특별한 수업이 이 한 권에 모두 담겼다.', '요시다 세이지 (지은이), 김도연 (옮긴이)', '서울미디어코믹스(서울문화사)', 'https://image.aladin.co.kr/product/34386/39/cover200/k592932508_1.jpg', '국내도서>만화>만화그리기와 읽기>만화작법/일러스트', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('죽기 전에 꼭 가봐야 할 인생 여행지 - 킴스트래블 국내 여행 산문집', '지금껏 많이 알려지지 않았고 본 사람이 별로 없는 국내의 숨은 비경 39곳을 담았다. 인기 여행지이건 잘 알려지지 않은 여행지이건 이번 생에는 꼭 봐야 할 풍경과 순간을 소개했다. 언제, 어느 때에 찾아가야 하는지 등 그곳을 여행하는 동안 보고 듣고 느꼈던 것들을 모두 담았다.', '킴스트래블 (지은이)', '용감한까치', 'https://image.aladin.co.kr/product/34360/64/cover200/k802932909_1.jpg', '국내도서>여행>국내 여행에세이', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('생각한다는 것 - 그리스도인으로 생각하며 살아가기', '철학은 물음에서 시작하고, 신학자는 기도하는 사람이라는 것이 강영안 교수의 생각이다. 동서고금의 사상과 사유의 깊이와 넓이를 알지만, 구체적이고 소박한 일상의 ‘복음적 생각’으로 안내하는 우리 시대의 다정한 철학자이다. 저자는 질문하는 신앙으로 살아가기를 권하고, 그 출발 지점에 ‘생각하며 살고 있나요?’라고 친절하게 묻는다.', '강영안 (지은이)', '두란노', 'https://image.aladin.co.kr/product/34351/79/cover200/8953148758_1.jpg', '국내도서>종교/역학>기독교(개신교)>기독교(개신교) 신앙생활>간증/영적성장', 1, 1);
