INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('귀신들의 땅', '국내에 처음으로 소개되는 타이완의 젊은 거장 천쓰홍의 장편 소설 『귀신들의 땅』이 민음사에서 출간되었다. 한 일가족을 중심으로 타이완의 아픈 현대사를 담아낸 걸작 『귀신들의 땅』은 타이완에서 가장 큰 양대 문학상인 ‘금장상 문학도서부문상’과 ‘금전상 연도백만대상’을 수상했으며, 12개 언어로 출간되어 《뉴욕타임스》 베스트셀러에 오르기도 했다.', '천쓰홍 (지은이), 김태성 (옮긴이)', '민음사', 'https://image.aladin.co.kr/product/33119/32/cover200/8937454823_1.jpg', '국내도서>소설/시/희곡>중국소설', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('샤워젤과 소다수', '체념과 무기력만 남은 듯한 세상에 희망이라는 농담을 던지며 자신을 향한 믿음을 놓지 않는 청년 세대를 그리는 시인, 고선경의 첫번째 시집 『샤워젤과 소다수』를 문학동네시인선 202번으로 출간한다.', '고선경 (지은이)', '문학동네', 'https://image.aladin.co.kr/product/32630/63/cover200/8954696015_1.jpg', '국내도서>소설/시/희곡>시>한국시', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('기분이 태도가 되지 말자 - 감정조절이 필요한 당신을 위한 책', '인생을 결정하는 건 바로 하루의 기분이다. 그날의 기분이 하루의 성과를 결정하고,하루의 성과들이 모여 미래를 만들고그 미래들이 곧 내 인생이 되기 때문이다. 감정을 조절하면 미래가 바뀐다.', '김수현(훈글) (지은이)', '하이스트', 'https://image.aladin.co.kr/product/30571/67/cover200/k892830719_3.jpg', '국내도서>에세이>한국에세이', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('왜 나는 너를 사랑하는가 (70만 부 기념 리커버)', '알랭 드 보통의 대표작 『왜 나는 너를 사랑하는가』가 70만 부 판매를 기념하여 산뜻한 표지로 새롭게 출간되었다. 런던과 파리를 오가는 비행기에서 우연히 만나 순식간에 사랑에 빠진 두 젊은이에 관한 이 소설은 연인이라는 특별한 관계와 사랑의 감정을 놀라운 깊이로 그려낸다.', '알랭 드 보통 (지은이), 정영목 (옮긴이)', '청미래', 'https://image.aladin.co.kr/product/30466/25/cover200/8986836831_1.jpg', '국내도서>소설/시/희곡>영미소설', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('다정한 것이 살아남는다 - 친화력으로 세상을 바꾸는 인류의 진화에 관하여', '늑대는 멸종 위기에 처했는데, 같은 조상에서 갈라져 나온 개는 어떻게 개체 수를 늘려나갈 수 있었을까? 사나운 침팬지보다 다정한 보노보가 더 성공적으로 번식할 수 있던 이유는? 브라이언 헤어와 버네사 우즈는 이에 대해 ‘다정한 것이 살아남는다’라는 답을 내놓는다.', '브라이언 헤어, 버네사 우즈 (지은이), 이민아 (옮긴이), 박한선 (감수)', '디플롯', 'https://image.aladin.co.kr/product/27608/83/cover200/k722733028_3.jpg', '국내도서>과학>기초과학/교양과학', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('악의', '히가시노 게이고의 대표작 ''가가 형사 시리즈''가 한국 출간 10여 년 만에 완전히 새로운 모습으로 독자들을 만난다. &lt;악의&gt;는 ''가가 형사 시리즈'' 3번째 작품으로, 히가시노 게이고의 초기 대표작이자 많은 미스터리 팬들의 필독서 겸 입문서로 사랑받는 소설이다.', '히가시노 게이고 (지은이), 양윤옥 (옮긴이)', '현대문학', 'https://image.aladin.co.kr/product/19822/35/cover200/8972750034_2.jpg', '국내도서>소설/시/희곡>추리/미스터리소설>일본 추리/미스터리소설', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('살육에 이르는 병', '‘죽음에 이르는 병-절망’으로 유명한 쇠렌 키르케고르의 저서에서 제목을 빌려온 ''살육에 이르는 병''의 첫 장면은 연쇄 살인사건의 범인 미노루가 검거되는 것으로 시작된다. 이 파격적이고도 대담한 배치는 이상심리로 살인을 저지르는 범인의 행적과 그 수사과정에 독자가 완벽하게 몰입할 수 있도록 돕는다.', '아비코 다케마루 (지은이), 권일영 (옮긴이)', '검은숲', 'https://image.aladin.co.kr/product/8557/28/cover200/8952776046_1.jpg', '국내도서>소설/시/희곡>추리/미스터리소설>일본 추리/미스터리소설', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('정의란 무엇인가 - 한국 200만 부 돌파, 37개국에서 출간된 세계적 베스트셀러', '구제 금융, 대리 출산, 동성 결혼, 과거사 공개 사과 등 현대 사회에서 우리가 흔히 부딪히는 문제를 통해 ‘무엇이 정의로운가’에 대한 해답을 탐구했다. 이 책은 탁월한 정치 철학자들이 남긴 시대를 초월한 철학적인 질문을 알기 쉽게 소개한다.', '마이클 샌델 (지은이), 김명철 (옮긴이), 김선욱 (감수)', '와이즈베리', 'https://image.aladin.co.kr/product/4898/84/cover200/8937834790_3.jpg', '국내도서>인문학>서양철학>윤리학/도덕철학', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('이 결혼은 어차피 망하게 되어 있다 5', '열감기로 인해 갑자기 쓰러진 카셀이 관저로 돌아오자 이를 본 이네스는카셀이 자신 때문에 쓰러진 거라 생각해 계속 그의 곁을 지키며 걱정한다. 이후 깨어난 카셀은 자신이 병석에 누워있을 때 이성을 잃고 행동하던 이네스의 모습에 다시 한번 애정을 느낀다.', '김차차 (원작), 청과 (그림), 초캄 (각색)', '영컴(YOUNG COM)', 'https://image.aladin.co.kr/product/34573/40/cover200/k712933369_1.jpg', '국내도서>만화>인터넷 연재 만화', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('2025 이동기 영어 하루 프로젝트 : 프로젝트 1. Foundation 문법강화 - 공무원 영어의 시작과 끝', '매일 부담 없는 학습량으로 꾸준한 영어 학습이 가능하도록 고안된 교재이다. 하루 10문제씩 일주일에 4번, 8주 동안 꾸준히 문제를 풀면서 이론을 실전에 적용하고 반복하여 학습 효과를 높일 수 있다.', '(주)이앤미래(대표 이동기) (지은이)', '에스티유니타스', 'https://image.aladin.co.kr/product/34539/73/cover200/k982932653_1.jpg', '국내도서>수험서/자격증>공무원 수험서>7/9급 공무원>영어>단어/문법/독해 외', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('문호 스트레이독스 공식 앤솔로지 6 : 목 - 노엔 코믹스', '탐정사 라디오를 진행하는 나카지마 아쓰시와 다자이 오사무, 새로운 옷을 사러 가는 포, ‘죽음의 천사’ 요사노 아키코가 탐정사에서 보호받을 때의 이야기까지. 호화로운 집필진이 본편과는 다른 맛의 「문스독」 세계를 그린다.', '아사기리 카프카 (원작), 하루카와 산고, 영에이스 편집부 (그림), 문기업 (옮긴이)', '영상출판미디어(주)', 'https://image.aladin.co.kr/product/34538/35/cover200/k902932054_1.jpg', '국내도서>만화>본격장르만화>판타지>액션 판타지', 1, 1);
