INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('여기는 루퐁이네 : 천사들의 시골살이', '쌈바요정 루디와 옭옭쟁이 퐁키는 약 225만의 구독자를 보유한 유튜브 채널 &lt;THE SOY 루퐁이네&gt;의 주인공으로, 2015년에 태어난 포메라니안 자매다. 차분한 루디와 발랄한 퐁키의 영상을 통해서 강아지들의 일상과 성격과 성향이 다른 강아지 자매의 모습을 관찰할 수 있다. 이번 책에는 도시 강아지 루퐁이의 우당탕탕 시골살이를 담았다. 새집에서 행복을 만들어 갈 루퐁이를 만나 보자.', '루퐁이 (지은이), 샌드박스네트워크 (감수), 박지영 (구성)', '서울문화사', 'https://image.aladin.co.kr/product/34500/50/cover200/k562932753_1.jpg', '국내도서>어린이>TV/만화/영화>만화 일반', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('전남친 최애음식 매장위원회', '실패한 연애, 이루어지지 못한 사랑, 그리움으로 남은 반려까지, 이제는 뒤로하고 앞으로 나아가고 싶은 모든 사람들을 위한 유쾌하면서도 찡한 이야기가 도착했다. 추억의 음식을 먹으며 마음에 고여 있던 감정을 묻어 버리는 이들의 사연을 그린 『전남친 최애음식 매장위원회』이다.', '가와시로 사키 (지은이), 황국영 (옮긴이)', '놀', 'https://image.aladin.co.kr/product/34474/39/cover200/k022932633_1.jpg', '국내도서>소설/시/희곡>일본소설>1950년대 이후 일본소설', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('미망 1~3 세트 - 전3권', '1990년 초판을 출간한 박완서 작가의 장편소설  『미망』(전 3권)이 2024년 민음사에서 새롭게 출간되었다. 『미망』은 총 3권으로 이루어진 흔치 않은 대작으로, 조선 말부터 일제강점기를 지나 한국전쟁 이후 분단에 이르기까지 개성의 한 중인 출신 상인 전처만 집안의 일대기를 다루고 있다.', '박완서 (지은이)', '민음사', 'https://image.aladin.co.kr/product/34444/56/cover200/8937457407_1.jpg', '국내도서>소설/시/희곡>한국소설>2000년대 이전 한국소설', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('버킷 리스트', '50여 년 동안 우리 곁에서 세상에 대한 ‘바라봄’을 시로 전해 온 나태주 시인, 이번에는 그가 시로 한 글자 한 글자 꾹꾹 눌러 쓴 버킷 리스트를 독자에게 전한다.', '나태주 (지은이), 지연리 (그림)', '열림원', 'https://image.aladin.co.kr/product/34433/80/cover200/k252932124_1.jpg', '국내도서>소설/시/희곡>시>한국시', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('2024 에듀윌 공인중개사 임선정 필살키 공인중개사법령 및 중개실무 - 최종이론&마무리 100선', '최근 출제경향을 분석해 시험에 나올 문제만 선별한 필살키 100문제를 수록하였다. 문제를 풀다가 부족한 이론 확인을 위해 각 문제마다 본 교재의 최종이론과 합격서 교재의 연계 페이지를 표시하였다.', '임선정 (지은이)', '에듀윌', 'https://image.aladin.co.kr/product/34427/81/cover200/k172932129_1.jpg', '국내도서>수험서/자격증>공인중개사/주택관리사>공인중개사 2차', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('노화를 늦추는 보고서 - 질병과 나이에 대한 통념을 바꾼 거장의 45년 연구', '노화와 질병에 관한 전 세계 최고 권위자인 하버드 심리학과 교수 엘렌 랭어가 우리의 젊음과 건강을 찾을 연구들을 담아 책으로 펴냈다. 질병 없이 건강하게 그리고 천천히 나이 들기 위해 많은 이들이 운동과 식단 등에 관심을 보인다. 랭어 교수는 이러한 방법들도 중요하지만 근본적으로 사고와 인식을 바꿔야 질병과 가속노화를 피할 수 있다고 강조한다.', '엘렌 랭어 (지은이), 신솔잎 (옮긴이)', '프런티어', 'https://image.aladin.co.kr/product/34371/99/cover200/8947549657_2.jpg', '국내도서>인문학>심리학/정신분석학>교양 심리학', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('취향은 어떻게 계급이 되는가 - 주어진 삶에서 벗어나 나만의 방향을 찾아주는 안내서', '오랜 시간 부르디외와 취향이라는 두 가지 단어 사이에 고심한 저자는 우리에게 중요한 질문을 건넨다. 일상을 구성하는 취향이란 무엇인가? 사회 속에서 취향은 어떻게 작동하는가? 이 책을 통해 이와 같은 질문들의 해답을 찾아가다 보면, 세상을 새롭게 바라보는 프레임이 생길 것이다.', '나영웅 (지은이)', '지음미디어', 'https://image.aladin.co.kr/product/34293/2/cover200/k852932279_1.jpg', '국내도서>인문학>교양 인문학', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('나는 얼마짜리입니까', '억울한 사연, 힘을 보태달라는 호소문, 웃음을 유발하는 위트 있는 일화, 따뜻한 감동을 주는 이야기가 매주 한겨레에 연재되었다. 여태껏 한번도 사회적 발언권을 가져보지 못한 이들의 목소리가 지면을 통해 사회에 발신된 것이다.', '6411의 목소리 (지은이), 노회찬재단 (기획)', '창비', 'https://image.aladin.co.kr/product/34221/80/cover200/893648043x_2.jpg', '국내도서>사회과학>사회문제>인권문제', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('퀸의 대각선 2', '베르나르 베르베르가 신작 소설 『퀸의 대각선』으로 돌아왔다. 개인의 뛰어난 역량이 인류 진보의 원동력이라고 믿는 모니카와, 함께 뭉친 집단이 역사를 움직인다고 믿는 니콜, 두 여성이 국제 정치 무대에서 격돌하면서 벌어지는 일을 다룬 작품이다.', '베르나르 베르베르 (지은이), 전미연 (옮긴이)', '열린책들', 'https://image.aladin.co.kr/product/34121/43/cover200/8932924406_1.jpg', '국내도서>소설/시/희곡>프랑스소설', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('읽는 기도 - 쉬지 말고 기도하라, 완전개정판', '예수님이 말씀으로 기도하신 것을 성경 속에서 찾아 말씀으로 기록한 기도문이다. 성막기도의 원리가 녹아있고 소리내어 읽기만 해도 기도의 불이 뜨겁게 임하여 여러분의 묵은 땅을 기경해 주는 강력한 기도문이다.', '무명의기도자 (지은이)', '더하트', 'https://image.aladin.co.kr/product/34014/93/cover200/k452931069_2.jpg', '국내도서>종교/역학>기독교(개신교)>기독교(개신교) 신앙생활>예배/기도/묵상(QT)', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('숙론 - 어떻게 마주 앉아 대화할 것인가', '우리 시대의 지성인 최재천 교수가 9년간 집필해 마침내 완성한 역작 《숙론》을 출간한다. 갈등과 분열을 거듭하는 시대에 우리는 어떻게 손잡을 수 있을까? 최재천 교수가 찾은 해법은 ‘숙론(熟論, Discourse)’이다.', '최재천 (지은이)', '김영사', 'https://image.aladin.co.kr/product/33915/47/cover200/8934950196_2.jpg', '국내도서>인문학>교양 인문학', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('퍼스널 MBA - 10주년 기념 증보판', '저자가 수천 권이 넘는 경영 서적을 읽고, 수백 명의 경영 전문가를 인터뷰하고, &lt;포춘&gt;지 선정 세계 500대 기업에서 일한 뒤, 1인 사업체서부터 수십만 명의 직원과 수십억 달러의 매출 규모를 지닌 다국적 기업에 이르기까지 다양한 기업을 컨설팅하며 배우고 경험한 지식을 모으고 정제하여 다듬은 뒤 몇 가지 개념으로 정리한, 경영 전반에 대한 핵심을 한 권에 집대성한 책이다', '조쉬 카우프만 (지은이), 박상진, 이상호 (옮긴이)', '진성북스', 'https://image.aladin.co.kr/product/33275/70/cover200/8997743600_1.jpg', '국내도서>경제경영>기업 경영>경영전략/혁신', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('최소한의 한국사 - 5천 년 역사가 단숨에 이해되는', '‘외워야 할 것 많고 어려운 한국사, 쉽고 빠르고 재미있게 핵심 내용만 알 수 없을까?’ 많은 사람이 역사 공부에 앞서 품는 이 질문에 답하기 위해 대한민국 대표 역사 강사 최태성이 나섰다. 한국사 교과서 저자이자 누적 수강생 700만 명에 이르는 한국사 1등 강사로서 복잡하고 어려운 말 없이 우리 역사에서 반드시 알아야 할 핵심 한국사를 엄선해 한 권에 담았다.', '최태성 (지은이)', '프런트페이지', 'https://image.aladin.co.kr/product/31888/53/cover200/k452833633_2.jpg', '국내도서>역사>한국사 일반', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('바다가 들리는 편의점', '24시간 불이 꺼지지 않는 일상적 공간인 편의점을 무대로 나이, 성별, 취향, 사연, 그리고 편의점을 찾는 목적까지 제각각인 손님들과 범상치 않은 직원들이 만들어 가는 따뜻하고 감동적인 작품이다. 장별로 다른 테마와 인물이 등장하는 옴니버스 형식이다.', '마치다 소노코 (지은이), 황국영 (옮긴이)', '모모', 'https://image.aladin.co.kr/product/31399/67/cover200/k452832203_1.jpg', '국내도서>소설/시/희곡>일본소설>1950년대 이후 일본소설', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('TEPS 서울대 텝스관리위원회 공식 기출문제집 : 출제기관 공식 수험서 - 뉴텝스 최신 기출 5회분 독점 출간 | 모바일 자동 채점 및 성적 분석 서비스 | 무료 실전용/복습용 MP3 | 무료 텝스 적중예상특강', '서울대 텝스관리위원회에서 독점으로 공개한 최신 기출문제 5회분을 통해 텝스 실전 감각을 익힐 수 있다. 실제 텝스 시험 음원을 다양한 버전으로 제공하여, 어떠한 시험 상황에도 철저하게 대비할 수 있다. [오답분석], [텝스 치트키] 학습 코너를 통해, 정답/오답의 근거를 확실히 잡고 넘어갈 수 있다.', '서울대학교 TEPS 관리위원회, 해커스 텝스연구소', '챔프스터디', 'https://image.aladin.co.kr/product/30740/49/cover200/8969653376_1.jpg', '국내도서>외국어>텝스>실전', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('똑게육아 - 영유아 수면교육', '‘로리’라는 필명으로 유명한 이 책의 저자 김준희 대표는 대한민국 영유아 수면교육 분야에 큰 획을 그은 인물로 꼽힌다. 대한민국의 육아는 ‘똑게육아’ 전 세대 vs. 후 세대로 나뉜다고 할 정도로, ‘똑게육아’는 2011년도의 연재 당시부터 10년 이상 꾸준한 사랑을 받아온 육아 바이블이다.', '로리(김준희) (지은이)', '북로스트', 'https://image.aladin.co.kr/product/28926/93/cover200/k432836219_2.jpg', '국내도서>좋은부모>육아>성장/발달', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('JLPT 일본어능력시험 한권으로 끝내기 N4 - 최신 개정판', '2010년부터 2021년까지 실제 시험에 출제된 어휘와 문법이 실려 있으며 그에 맞는 문제 유형과 문제로 구성된 JLPT(일본어능력시험) N4 수험서다. 네 가지 시험 영역을 모두 다루고 있어 종합적으로 JLPT N4를 마스터할 수 있다.', '이치우, 이한나 (지은이)', '다락원', 'https://image.aladin.co.kr/product/27810/7/cover200/8927712447_3.jpg', '국내도서>외국어>일본어>JLPT>N4', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('해커스 토익 스타트 RC Reading (리딩) 입문서 - 최신기출경향 반영 | 초보를 위한 토익 리딩 입문서 | 기초 문법ㆍ어휘ㆍ독해 4주 완성 [실전모의고사(별책)+ 단어암기장&MP3 제공]', '해커스 토익 스타트 Reading 2020 최신개정판. 토익 기초 문법.어휘.독해 4주 완성 교재로 최신기출경향을 완벽하게 분석하고 반영했다. 지문 끊어 읽기와 상세한 해설로 모든 지문 및 문제를 꼼꼼하게 학습할 수 있다. 독해 문제를 명쾌하게 해결해주는 문제풀이 전략과 실전 대비를 위한 실전모의고사(별책) 1회분 을 제공한다.', 'David Cho (지은이)', '해커스어학연구소(Hackers)', 'https://image.aladin.co.kr/product/22236/30/cover200/8965423376_3.jpg', '국내도서>외국어>토익>Reading', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('비관하는 힘', '공학박사 겸 소설가 모리 히로시는 인간의 생존과 번영의 비법을 ‘비관’이라고 주장한다. 그동안 당연한 듯 낙관에 젖은 사회에 던지는 경고와 동시에 조금만 우리의 현실을냉정하게 바라보자는 제안을 담은 책이다.', '모리 히로시 (지은이), 홍성민 (옮긴이)', '더난출판사', 'https://image.aladin.co.kr/product/20774/45/cover200/8984059730_1.jpg', '국내도서>인문학>교양 인문학', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('회복탄력성 (15만부 기념 리커버) - 시련을 행운으로 바꾸는 마음 근력의 힘', '15만부 기념 리커버판. 회복탄력성으로 어려움을 이겨낸 사람들의 사례를 소개하고, 수십 년 간 이어온 회복탄력성에 대한 연구결과를 제시해 설득력을 높인다. 그리고 모든 독자가 자신의 회복탄력성 지수를 진단하여 회복탄력성을 키울 수 있도록 이끌어준다.', '김주환 (지은이)', '위즈덤하우스', 'https://image.aladin.co.kr/product/18754/45/cover200/k522635176_2.jpg', '국내도서>자기계발>성공>성공학', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('지리의 힘 - 지리는 어떻게 개인의 운명을, 세계사를, 세계 경제를 좌우하는가', '영국 &lt;파이낸셜 타임스&gt;의 터키 특파원과 스카이 뉴스 외교 부문 에디터와 BBC 기자로도 일하는 등 25년 이상 30개 이상의 분쟁 지역을 직접 현장에서 취재하면서 국제 문제 전문 저널리스트로 활동해온 저자가 ''지리라는 렌즈''를 통해 세계를 조망한 책이다.', '팀 마샬 (지은이), 김미선 (옮긴이)', '사이', 'https://image.aladin.co.kr/product/8864/8/cover200/8993178690_2.jpg', '국내도서>사회과학>지리학/지정학>지리학', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('깡통 소년 - SF 미스터리, 4단계', '혼자 살고 있는 ''엉뚱한'' 아줌마 바톨로티 부인에게 어느 날 커다란 소포가 배달된다. 소포 안에 들어 있는 것은 커다란 은색 깡통. 그 깡통에서 나타난 것은 여덟 살 된 남자 아이이다.', '크리스티네 뇌스틀링거 (지은이), 프란츠 비트캄프 (그림), 유혜자 (옮긴이)', '미래엔아이세움', 'https://image.aladin.co.kr/product/27/89/cover200/8937840111_2.jpg', '국내도서>어린이>동화/명작/고전>외국창작동화', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('수학이 생명의 언어라면 - 수면부터 생체 리듬, 팬데믹, 신약 개발까지, 생명을 해독하는 수리생물학의 세계', '수리생물학의 최전선에서 수리 모델을 이용해 생명 현상을 탐구하는 KAIST 수리과학과 교수이자 수학자인 저자는 이 책에서 생체 리듬, 신약 개발, 수면 패턴, 팬데믹 등에 관한 다양한 문제에 수학이 실제로 어떻게 활용되고 있는지를 친절하게 설명한다.', '김재경 (지은이)', '동아시아', 'https://image.aladin.co.kr/product/34554/83/cover200/8962624958_1.jpg', '국내도서>과학>수학>수학 일반', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('어느 심리학자와 사기꾼의 대화 - 현혹과 압박의 기술에 당하지 않고 타인의 진짜 속마음을 꿰뚫어 본다!', '《마음 설계의 힘》을 쓴 임철웅 심리대화 전문가의 새 책, 《어느 심리학자와 사기꾼의 대화》가 출간되었다. 저자는 이번 책에서 ‘타인의 심리를 꿰뚫어 보는 4개 분야 전문가가 구사하는 최적의 심리대화법’을 낱낱이 밝혔다.', '임철웅 (지은이)', '트로이목마', 'https://image.aladin.co.kr/product/34539/96/cover200/k332932653_1.jpg', '국내도서>자기계발>인간관계>교양심리학', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('기암관의 살인', '일용직 친구였던 도쿠나가가 사라지고 그가 남긴 단서를 추적하던 중 수상한 고수익 아르바이트를 발견한 일용직 노동자 청년 ‘사토’. 가벼운 마음으로 지원한 ‘사토’는 카리브해의 외딴섬에서 기암관 이라 불리는 저택에 묵게 되는데….', '다카노 유시 (지은이), 송현정 (옮긴이)', '허밍북스', 'https://image.aladin.co.kr/product/34530/78/cover200/k222932953_1.jpg', '국내도서>소설/시/희곡>추리/미스터리소설>일본 추리/미스터리소설', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('혀 끝에서부터 사랑 1', '‘포크’인 타츠나리는 어느 날 하교 중에 과묵한 우등생 후배 나루세를 불의의 사고에서 구해주다가 그가 ‘케이크’라는 사실을 깨닫는다. 도와준 답례로 뭐든지 하겠다고 말하는 나루세에게, 거부당할 거라고 생각하면서도 「식사」를 하게 해달라고 요구하는 타츠나리. 그러나 생각 외로 선선히 승낙을 받아 나루세와 둘이 점심시간 옥상에서 「비밀의 식사」를 하게 되는데….', '모모세 안 (지은이)', '블랑코믹스(BLANC COMICS)', 'https://image.aladin.co.kr/product/34527/64/cover200/k952932856_1.jpg', '국내도서>만화>순정만화>그 남자들의 사랑', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('철학자의 문장들 - 나의 첫 철학 필사 노트', '쇼펜하우어, 니체와 하이데거까지 철학의 모든 것을 한 권에 담았다. 필사하기 좋은 철학적 단편 169개 엄선하여 수록하였다. 잘 알려진 철학자들과 꼭 알아야 할 철학자들을 모두 담았다. 세계적인 유명인들의 철학적 문장들을 함께 엮어 재밌다.', '김대웅 (엮은이)', '북플라자', 'https://image.aladin.co.kr/product/34500/73/cover200/k842932753_1.jpg', '국내도서>인문학>교양 인문학', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('오늘은 회색빛', '일상에서 느끼는 마음속 감정들을 다채로운 색으로 풀어냈다. 누구나 밝은 날도, 어두운 날도 있다는 것을 보여 주며, 상상의 힘으로 우리들 마음에 나타나는 크고 작은 감정의 요동을 자연스럽게 포용하고 넘어갈 수 있도록 돕는 그림책이다.', '로라 도크릴 (지은이), 로렌 차일드 (그림), 김지은 (옮긴이)', '웅진주니어', 'https://image.aladin.co.kr/product/34450/49/cover200/8901280817_1.jpg', '국내도서>유아>그림책>나라별 그림책>유럽', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('태초에 사랑이 있었다 - 삼위일체에 관하여', '20세기 정교회 신학을 대표하는 신학자인 두미트루 스터닐로에의 삼위일체 관련 저작. 영원, 시간, 사랑이라는 개념을 중심으로 그리스도교의 핵심 가르침인 삼위일체를 설명하고, 그 의미를 해설한다.', '두미트루 스터닐로에 (지은이), 김인수 (옮긴이)', '비아', 'https://image.aladin.co.kr/product/34448/31/cover200/k682932330_1.jpg', '국내도서>종교/역학>가톨릭>가톨릭 신학', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('빨간 점', '사계절그림책상 대상 《내마음 ㅅㅅㅎ》 김지영 작가의 새 그림책이다. 빨강 별색과 파랑, 이렇게 두 가지 색만을 사용하여 아이의 마음속에 자리 잡은 불안감과 심리적인 압박감을 섬세하면서도 유쾌하게 그려냈다.', '김지영 (지은이)', '길벗어린이', 'https://image.aladin.co.kr/product/34369/78/cover200/8955827695_1.jpg', '국내도서>유아>그림책>나라별 그림책>한국 그림책', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('진짜 기본 청소책 - 그대로 따라 하면 달라지는 우리집 구석구석 청소 레시피 90개', '청소를 주제로 16만여 명과 소통하는 살림 인플루언서 ‘두룸’. 그녀는 평소 간단한 청소팁이나 편리한 수납법, 신박한 살림용품을 찾아서 사용해 보기를 좋아했고, 이를 기록하고자 하나둘 SNS에 올렸다. 이런 다양한 경험과 노하우를 담은 책이 바로 &lt;진짜 기본 청소책&gt;이다.', '정두미(두룸) (지은이)', '레시피팩토리', 'https://image.aladin.co.kr/product/34293/63/cover200/k252932271_1.jpg', '국내도서>요리/살림>살림/정리수납', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('색 잘 쓰는 디자이너 - 디자이너에게 영감을 주는 배색·디자인 아이디어 800가지', '주변 풍경을 찍은 ‘사진’에서 디자인 아이디어를 얻을 수 있다면? ‘색 좀 잘 쓰고 싶은 디자이너’를 위한 배색, 글꼴 선택, 변형 디자인 노하우.', '고바야시 레나 (지은이), 강아윤 (감수)', '이지스퍼블리싱', 'https://image.aladin.co.kr/product/34254/73/cover200/k072932860_1.jpg', '국내도서>컴퓨터/모바일>그래픽/멀티미디어>그래픽 일반', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('슈뻘맨의 숨은 과학 찾기 4 - 엉뚱 도전 속에 숨은 과학 상식', '삼각김밥으로 이색 요리 만들기, 슈뻘맨의 노래 실력 겨루기, 초대형 튜브 타고 물놀이하기 등 슈뻘맨만의 엉뚱하고 기발한 영상 콘텐츠를 흥미진진한 만화로 풀어 냈다. 더불어 주방 세제를 피해 갈라지는 후추 영상, 유성 매직으로 쓴 글씨를 오렌지 껍질로 지우는 영상 같이 인터넷에 떠도는 신기한 영상 속 내용이 사실인지 거짓인지 슈뻘맨이 직접 파헤쳐 보는 ‘톡톡재판소’ 코너까지 수록했다.', '슈뻘맨 (원작), 서후 (글), 류수형 (그림), 샌드박스네트워크, 정재형 (감수)', '미래엔아이세움', 'https://image.aladin.co.kr/product/34121/18/cover200/k102931996_1.jpg', '국내도서>어린이>과학/수학/컴퓨터>과학 일반', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('폭염 살인 - 폭주하는 더위는 어떻게 우리 삶을 파괴하는가', '진화의 속도를 넘어 폭주하는 더위, 그리고 그것이 불러올 예측 불허의 재앙 앞에서 에어컨의 냉기가 과연 언제까지 우리를 지켜줄 수 있을까. 분명한 건 극한 더위가 불러올 죽음의 연쇄 반응 앞에 그 누구도 자유로울 수 없다는 사실이다.', '제프 구델 (지은이), 왕수민 (옮긴이)', '웅진지식하우스', 'https://image.aladin.co.kr/product/34015/19/cover200/8901283476_1.jpg', '국내도서>과학>지구과학>기상학/기후', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('제로 학교', '샘터어린이문고 79권. 공감 능력이 부족한 사람들이 있는 ‘제로 학교’에서 펼쳐지는 네 가지 이야기를 담아냈다. 다른 사람의 감정과 상황에 자신 역시 그렇다고 느끼는 것, 소통의 기본이라 할 수 있는 ‘공감’에 대한 이해와 방법을 꿈과 취미, 추억과 호기심 등의 소재로 자연스럽게 이야기에 녹여낸 아이들의 마음 성장기다.', '박남희, 이여니, 곽윤숙, 김태호, 김연제 (지은이)', '샘터사', 'https://image.aladin.co.kr/product/34011/61/cover200/8946474459_1.jpg', '국내도서>어린이>동화/명작/고전>국내창작동화', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('AI 사피엔스 - 전혀 다른 세상의 인류', '코로나 팬데믹을 거치면서 더욱 진화한 포노 사피엔스가 AI라는 전대미문의 신무기를 장착하고 어떻게 천지개벽급 신문명을 만들고 있는지를 세세하게 포착했다. 지금 우리가 유심히 관찰할 것은, 일상의 변화부터 각 산업의 구조적 진화까지 이끌어가는 거대한 축의 전환과 새로운 문명의 시작이다.', '최재붕 (지은이)', '쌤앤파커스', 'https://image.aladin.co.kr/product/33927/67/cover200/k342930036_2.jpg', '국내도서>경제경영>트렌드/미래전망>트렌드/미래전망 일반', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('결혼·여름', '‘책세상 카뮈 전집 개정판’ 7권. 1939년에 출간된 《결혼(Noces)》과 1954년에 출간한 《여름(L''&#201;t&#233;)》을 담은 알베르 카뮈의 여행에세이다. 기존 전면 개정판에서 일부 번역과 표현을 고쳐, 번역본의 생명력을 불어넣었다.', '알베르 카뮈 (지은이), 김화영 (옮긴이)', '책세상', 'https://image.aladin.co.kr/product/33897/29/cover200/k012930823_3.jpg', '국내도서>에세이>외국에세이', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('몰입 확장판 - 인생을 바꾸는 자기 혁명', '2007년 출간되어 전 국민에게 ‘몰입’ 열풍을 불러일으킨 베스트셀러 『몰입』이 전면 개정되어 돌아왔다. 이번 확장판은 단순히 290쪽에서 456쪽으로 분량을 추가한 것에 그치지 않고, 몰입을 통해 17년간 축적된 수만 건의 데이터와 뇌과학 지식, 변화한 시대상을 반영하여 초판의 내용을 전면 업그레이드해 더욱 특별하다.', '황농문 (지은이)', '알에이치코리아(RHK)', 'https://image.aladin.co.kr/product/33850/89/cover200/8925575027_1.jpg', '국내도서>자기계발>성공>성공학', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('맞춤법에 진심인 편 - 자꾸 틀리고 헷갈리는 필수 표현 100가지', '제대로 알고 싶지만 어렵게만 느껴지는 맞춤법을 누구나 공감하는 일상 속 상황들로 재미있고 명쾌하게 정리해 주는 책이다. 35만 구독자를 보유한 유튜버이자 대치동 국어 강사로 활동했던 저자는 자신만의 기발한 맞춤법 공식을 이 책에 담았다.', '차민진(밍찌) (지은이), 이혜원 (그림)', '풀빛', 'https://image.aladin.co.kr/product/33819/54/cover200/k592930791_1.jpg', '국내도서>청소년>청소년 자기계발', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('사람을 안다는 것', '어떻게 하면 우리 삶에서 관계로 인한 기쁨을 만끽할 수 있을지 구체적인 방법을 제시한다. 사람을 대할 때 지극히 소극적이고 방어적이었던 브룩스는 상대방을 깊이 알게 되는 경험을 통해 조금씩 변한다. 사람을 아주 깊숙이 알아가는 일이 상대방과 나 자신의 세계를 어떻게 넓혀가는지에 대한 경험과 연구, 사례들이 다양하게 펼쳐진다.', '데이비드 브룩스 (지은이), 이경식 (옮긴이)', '웅진지식하우스', 'https://image.aladin.co.kr/product/33693/7/cover200/8901280779_1.jpg', '국내도서>인문학>인문 에세이', 1, 1);
