INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('홈·브런치', '도쿄를 넘어 전 세계인의 입맛을 사로잡은 10평 남짓한 작은 빵집, 베이커리 ‘365일’의 시그니처 브런치 레시피북. 누구나 손쉽게 ‘365일’의 브런치를 만들 수 있도록 간단한 토스트부터 빵과 함께할 때 가장 맛있는 달걀 요리, 다양한 재료를 넣은 샌드위치와 타르틴 등을 소개한다.', '스기쿠보 아키마사 (지은이), 임지인 (옮긴이)', '나무수', 'https://image.aladin.co.kr/product/25361/90/cover200/8968332789_1.jpg', '국내도서>요리/살림>생활요리', 1, 1);
