INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('여름은 오래 그곳에 남아', '1982년 일본의 고급 별장지 가루이자와. ‘무라이 건축 설계 사무소‘는 여름 한철을 그곳 아사마 산 자락의 별장에서 보낸다. 삶과 맞닿은 건축을 꿈꾸는 사람들과 언제까지고 계속되었으면 했던 그 여름의 고아했던 나날. 이윽고 국립현대도서관 설계 경합을 앞두고 뜨거운 분투가 시작되는데.', '마쓰이에 마사시 (지은이), 김춘미 (옮긴이)', '비채', 'https://image.aladin.co.kr/product/7492/9/cover200/8934972203_1.jpg', '국내도서>소설/시/희곡>일본소설>1950년대 이후 일본소설', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('E=mc² - 세상에서 가장 유명한 방정식의 일생', '“에너지는 질량과 빛의 속도의 제곱을 곱한 것과 같다.” 세상에서 가장 유명하고 아름다운 방정식 E=mc². 1905년, 이제 막 20세기가 시작했을 때, 스물여섯 살 무명 과학자였던 아인슈타인의 머릿속에 떠오른 이 짤막한 방정식이 어떻게 세계를 뒤흔들게 되었을까?', '데이비드 보더니스 (지은이), 김희봉 (옮긴이)', '웅진지식하우스', 'https://image.aladin.co.kr/product/4429/95/cover200/8901165856_1.jpg', '국내도서>과학>물리학>쉽게 배우는 물리학', 1, 1);
