INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('2025 조태정 영어 데일리루틴 베이직 2 - 영어는 결국 꾸준한 루틴으로 완성하는 것', '공무원 영어를 대비할 수 있는 수험서다. 하루에 스스로 연습하면 좋을 어휘 2문제, 문법 3문제, 독해 5문제를 구성하여 “하프 모의고사” 형태로 총 DAY 16 분량을 수록하였다. 매일 적정 비율로, 출제 경향을 반영하여 구성된 10문제를 꾸준히 학습할 수 있다.', '조태정 (지은이)', '사피엔스넷', 'https://image.aladin.co.kr/product/34470/92/cover200/k452932536_1.jpg', '국내도서>수험서/자격증>공무원 수험서>7/9급 공무원>영어>기출/문제/모의고사', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('2025 이기적 컴퓨터활용능력 2급 실기 기본서 - 동영상 강의 전강, 자동 채점 서비스', '2025 이기적 컴퓨터활용능력 2급 실기 기본서. 출제기준을 분석, 반영하여 컴퓨터활용능력 실기 시험에 대비할 수 있도록 구성하였다. 1권의 이론서에서 핵심이론과 함수사전을 공부한 후, 2권의 문제집에서 상시 기출 문제&기출 유형 문제를 풀어보자.', '박윤정 (지은이)', '영진.com(영진닷컴)', 'https://image.aladin.co.kr/product/34451/34/cover200/8931475918_1.jpg', '국내도서>컴퓨터/모바일>활용능력>컴퓨터활용능력>컴퓨터활용능력 실기', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('팔견전 19 - 동방팔견이문, S코믹스', '리오와 나치에 이어 카나메와 아야네가 음모가 소용돌이치는 황궁으로 호출받는다. 그러나 아야네 대신 하마지가 황궁으로 향한 것을 알게 된 시노와 소스케는, 하마지를 구하고자 황궁으로 잠입하는데...', '아베 미유키 (지은이), 박소현 (옮긴이)', '㈜소미미디어', 'https://image.aladin.co.kr/product/34445/28/cover200/k222932236_1.jpg', '국내도서>만화>본격장르만화>판타지>액션 판타지', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('관세음보살이여, 관세음보살이여 - 당신은 누구시길래 늘 내 편이 되어 나를 도와주시나요', '관세음보살이 언제나 우리를 지켜주었음을 강조한다. 어느 하나의 형태로 고정된 것이 아니라, 그때그때 중생을 도울 수 있는 가장 적절한 모습으로 몸을 바꾸어 나타난다. 이렇게 수많은 형태로 등장하는 관세음보살의 화신을 ‘변화관음(變化觀音)’이라 부른다.', '김호성 (지은이)', '불광출판사', 'https://image.aladin.co.kr/product/34425/65/cover200/k842932025_1.jpg', '국내도서>종교/역학>불교>불교의 이해', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('2024 에듀윌 공인중개사 한영규 필살키 부동산세법 - 최종이론&마무리 100선', '에듀윌 공인중개사 대표 교수님들의 필살기를 모아 만든 합격 문을 여는 열쇠(key)! 시험 문제를 풀 때 필요한 필수이론만 압축하여 수록! 합격을 위해 엄선된 파이널 100문제 수록! 이론&문제 한 권으로 정리 끝! 시험장까지 가져가는 마지막 한 권!', '한영규 (지은이)', '에듀윌', 'https://image.aladin.co.kr/product/34390/72/cover200/k262932506_1.jpg', '국내도서>수험서/자격증>공인중개사/주택관리사>공인중개사 2차', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('엄마의 여름 방학', '여름 방학을 맞은 아이는 엄마의 보물 상자 속에서 찾아낸 그림일기장을 들여다보며 엄마와 함께 과거로 추억 여행을 떠난다. 1995년 8월, 엄마는 처음으로 언니와 단둘이 기차를 타고 외갓집에 간다. 기차역에 마중 나온 할아버지를 따라 외갓집 마당으로 들어서자, 강아지 세리가 반갑게 달려 나와 꼬리를 흔들어 준다. 그런 세리와 달리 외사촌 준일이와 준영이는 멀찍이 서서 쭈뼛거리기만 하는데…….', '김유진 (지은이)', '책읽는곰', 'https://image.aladin.co.kr/product/34353/65/cover200/k392932098_1.jpg', '국내도서>유아>그림책>나라별 그림책>한국 그림책', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('역사의 쓸모 (개정증보판) - 자유롭고 떳떳한 삶을 위한 23가지 통찰', '30만 부 가까이 판매된 베스트셀러 《역사의 쓸모》의 개정증보판이다. 이번 개정증보판에는 지난 5년간의 사회 변화 속에서 저자가 새롭게 찾은 역사의 쓸모를 소개한 글(‘위기를 극복해 온 인류의 생존법’)이 추가되어 23가지 통찰을 전한다.', '최태성 (지은이)', '프런트페이지', 'https://image.aladin.co.kr/product/34323/80/cover200/k392932480_3.jpg', '국내도서>역사>역사학>역사학 일반', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('당신의 불안은 죄가 없다 - 걱정 많고 불안한 당신을 위한 뇌과학 처방전', '세계적인 신경과학자 웬디 스즈키는 최신작 《당신의 불안은 죄가 없다》에서 저자는 자신이 불안에 빠져들며 경험했던 사실과 뇌과학적 근거를 통해 불안에 대한 오해를 완전히 풀어준다. 그야말로 불안을 ‘재발견’하게 해준다.', '웬디 스즈키 (지은이), 안젤라 센 (옮긴이)', '21세기북스', 'https://image.aladin.co.kr/product/34316/82/cover200/k492932578_1.jpg', '국내도서>인문학>심리학/정신분석학>교양 심리학', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('2025 심슨 구문', '2025년부터 개편되는 공무원 영어 시험 출제 기조를 반영하여 ‘지엽 탈피’와 ‘추론 강화’라는 두 방향성을 중심으로 개정된다. 이중 『심슨 구문』은 『심슨 영어』의 두 번째 기본서로, 아무리 복잡한 문장이라도 쉽게 분석할 수 있도록 영어 해석을 패턴화하여 구문 기본기를 다질 수 있도록 했다.', '심우철 (지은이)', '심슨북스', 'https://image.aladin.co.kr/product/34196/61/cover200/k642931412_1.jpg', '국내도서>수험서/자격증>공무원 수험서>7/9급 공무원>영어>단어/문법/독해 외', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('이렇게 작지만 확실한 행복 - 개정판', '무라카미 하루키가 보스턴 근교의 대학 마을 케임브리지에서 보낸 2년간의 생활을 솔직하게 드러낸다. 보스턴 마라톤에 참가한 이야기, 자동차를 도난당해 보험회사 여직원과 실랑이를 벌인 이야기, 이웃집 고양이에 대한 소식, 중국과 몽골을 여행할 때 곤혹스러웠던 음식 알레르기 같은 일상의 단상을 담았다.', '무라카미 하루키 (지은이), 안자이 미즈마루 (그림), 김진욱 (옮긴이), 무라카미 요코 (사진)', '문학사상', 'https://image.aladin.co.kr/product/34082/41/cover200/8970125930_1.jpg', '국내도서>에세이>사진/그림 에세이', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('최고의 프롬프트 엔지니어링 강의 - 개념 이해로 시작하기 좋은', '프롬프트 엔지니어링의 방대한 내용을 프로그래밍 지식이 없는 사람도 이해하고 접근하기 쉽도록 컴팩트하고 짜임새 있게 전달한다. 인공지능 시대를 살아가는 데 꼭 필요한 상식과도 같은 프롬프트 엔지니어링을 기초 개념부터 실습까지 누구나 체계적으로 학습할 수 있도록 했다.', '김진중 (지은이)', '리코멘드', 'https://image.aladin.co.kr/product/34005/72/cover200/k772931869_1.jpg', '국내도서>컴퓨터/모바일>컴퓨터 공학>자료구조/알고리즘', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('홀썸의 집밥 예찬 - 매일의 건강 집밥이 불러온 놀라운 일상의 기적', '유수 대학 논문과 책, 강의를 통해 얻은 건강 지식과 저자가 일상에서 쌓아온 다양한 경험을 바탕으로 직접 개발하고 실천 중인 레시피를 개발했다. 최대한 가공식품의 사용을 피하고, 누구나 손쉽게 따라 만들 수 있는 건강한 집밥 레시피를 꾸준히 개발하여 모두 소개했다.', '홀썸모먼트 (지은이)', '다산라이프', 'https://image.aladin.co.kr/product/33970/91/cover200/k612930441_2.jpg', '국내도서>요리/살림>생활요리', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('녹나무의 여신', '2020년 3월 히가시노 게이고의 작가 데뷔 35주년을 기념해 전 세계에 동시 출간된 《녹나무의 파수꾼》의 속편 《녹나무의 여신》이 이번에도 일본과 동시 출간된다. 레이토가 여러 사람과 만나 우여곡절을 거듭하며 기적의 새로운 의미를 찾아가는 내용을 담았다.', '히가시노 게이고 (지은이), 양윤옥 (옮긴이)', '㈜소미미디어', 'https://image.aladin.co.kr/product/33953/42/cover200/s342931413_1.jpg', '국내도서>소설/시/희곡>일본소설>1950년대 이후 일본소설', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('도파민네이션 (리미티드 에디션) - 쾌락 과잉 시대에서 균형 찾기', '2021년 미국에서 출간돼 지금까지 뜨거운 논쟁을 일으키고 있는 『도파민네이션』은 인간이 중독에 빠지는 이유를 의지, 도덕성의 결핍이 아니라 쾌락과 고통을 지휘하는 신경물질, 도파민에서 찾고 있다.', '애나 렘키 (지은이), 김두완 (옮긴이)', '흐름출판', 'https://image.aladin.co.kr/product/33927/58/cover200/k122930036_1.jpg', '국내도서>인문학>교양 인문학', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('나 혼자만 레벨업 11 - 만화', '국가 권력급 헌터 살해 사건의 범인으로 지목된 용의자 S. 모든 정황이 용의자 S가 성진우의 아버지임을 가리키고, 진우는 모두를 적으로 돌리더라도 가족을 지키겠다 다짐한다. 한편, 고건희 협회장 앞에 나타난 정체를 알 수 없는 존재. 갑작스레 쏟아지는 공격에 고건희는 위험에 빠지게 되는데…!', '장성락(REDICE STUDIO) (지은이), 추공 (원작), 현군 (각색)', '디앤씨웹툰비즈', 'https://image.aladin.co.kr/product/33921/59/cover200/k852930930_1.jpg', '국내도서>만화>본격장르만화>판타지>드라마틱 판타지', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('부자의 그릇 (블랙 에디션) - 돈을 다루는 능력을 키우는 법', '2015년 출간 이후, 수많은 젊은 부자들의 ‘그릇’을 키운 바로 그 책 『부자의 그릇』이 블랙 에디션으로 돌아왔다. 이 책은 김봉진 戰 우아한형제들 의장, 청울림 유대열 대표, 하정우 배우 등이 강력 추천하고, 100곳이 넘는 유튜브 채널에서 소개된 바 있다.', '이즈미 마사토 (지은이), 김윤수 (옮긴이)', '다산북스', 'https://image.aladin.co.kr/product/33883/76/cover200/k422930319_1.jpg', '국내도서>경제경영>재테크/투자>재테크/투자 일반', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('침묵의 봄 - 개정증보판', '2024년은 레이첼 카슨이 세상을 떠난 지 60주기가 되는 해다. 개정증보판은 ‘제2의 레이첼 카슨’으로 평가받는 샌드라 스타인그래버의 서문과 《침묵의 봄》 출간 이후 환경 관련 글, 연보 등을 추가했다. 《침묵의 봄》을 더 깊이 이해하고, 카슨을 좀더 알 수 있는 계기가 될 것이다.', '레이첼 카슨 (지은이), 김은령 (옮긴이), 홍욱희 (감수)', '에코리브르', 'https://image.aladin.co.kr/product/33853/72/cover200/8962632721_1.jpg', '국내도서>사회과학>환경/생태문제>환경학 일반', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('사카모토 데이즈 15', '괴력 효우 & 저격수 헤이스케 vs 자력술사 쿠마노미. 제각기 다른 전투 방식을 보여주는 세 사람이 펼치는 근접, 원거리가 뒤섞인 격전의 향방은?', '스즈키 유우토 (지은이)', '대원씨아이(만화)', 'https://image.aladin.co.kr/product/33799/8/cover200/k192930579_1.jpg', '국내도서>만화>코믹/명랑만화', 1, 1);
