INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('주술회전 25 - 인외마경 신주쿠 결전', '후시구로의 몸을 완전히 지배하기 위해, 요로즈가 강생한 츠미키를 살해하려 드는 스쿠나. 한편 이타도리 일행은 간신히 죽음을 면한 쿠루스와 천사의 힘으로 고죠의 부활을 시도한다! 사상 최강인 스쿠나와 현대 최강인 고죠… 규격을 벗어난 두 사람이 드디어 격돌하는데?!', '아쿠타미 게게 (지은이), 이정운 (옮긴이)', '서울미디어코믹스(서울문화사)', 'https://image.aladin.co.kr/product/33511/24/cover200/k062938559_1.jpg', '국내도서>만화>본격장르만화>판타지>액션 판타지', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('처음부터 시작하는 주식투자 단타전략 - 15만 원으로 10억 만든 실전투자대회 1위 수상자의 필승 트레이딩 공식', '카카오TV, 넷플릭스 방영 &lt개미는 오늘도 뚠뚠&gt 전 챕터 최고 수익률 133%, MZ세대 대왕개미의 투자법. 여의도 제도권 취업보다 전업투자를 선택하게 만든 안전한 주식 단기 매매 공식을 담은 책이다.', '홍인기 (지은이)', '길벗', 'https://image.aladin.co.kr/product/33034/23/cover200/k912937580_1.jpg', '국내도서>경제경영>재테크/투자>주식/펀드', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('남에게 보여주려고 인생을 낭비하지 마라 - 쇼펜하우어 소품집', '누구나 행복을 추구해야 한다는 ‘행복론’은 논쟁이 필요 없는 당연한 명제처럼 여겨진다. 하지만 쇼펜하우어는 이러한 전제에서 모든 불행이 생겨난다고 말한다.', '아르투어 쇼펜하우어 (지은이), 박제헌 (옮긴이)', '페이지2(page2)', 'https://image.aladin.co.kr/product/32648/45/cover200/k072935654_1.jpg', '국내도서>인문학>교양 인문학', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('범도 1 - 포수의 원칙', '신동엽문학상, 황순원문학상, 오영수문학상을 수상한 소설가 방현석이 다년간의 취재와 자료 조사를 거쳐 10년이 넘는 시간 동안 집필한 필생의 역작으로, 홍범도가 산짐승을 사냥하는 포수로서 산야를 떠돌다 항일 운동에 투신하여 각종 인간군상을 만나며 성장해나가는 과정을 그린 작품이다.', '방현석 (지은이)', '문학동네', 'https://image.aladin.co.kr/product/31747/79/cover200/895469327x_1.jpg', '국내도서>소설/시/희곡>역사소설>한국 역사소설', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('부자의 언어 (양장 리커버 골드씨드 에디션) - 어떻게 살아야 부자가 되는지 묻는 아들에게', '『부자의 언어』가 특별 에디션으로 출간되었다. 부에 대한 정의를 일깨워주고, 부를 향한 길로 안내하는 친절한 지침서로 롤러코스터 같은 인생에서 ‘경제적 자유’를 얻기 위해 노력한 한 사람의 스펙터클한 여정이 담겨 있다.', '존 소포릭 (지은이), 이한이 (옮긴이)', '윌북', 'https://image.aladin.co.kr/product/31458/92/cover200/k672832326_3.jpg', '국내도서>경제경영>재테크/투자>재테크/투자 일반', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('호밀밭의 파수꾼', '사립학교의 문제아 홀든 콜필드가 퇴학을 당하고 집으로 돌아오기까지 며칠간의 일들을 담은 작품이다. 십대들의 언어를 그대로 옮긴 듯한 욕설과 비속어 속에 위트를 간직한 문장으로 청춘만이 공감할 수 있는 페이소스를 녹여 낸 이 소설은 젊은 독자들 사이에서 ‘콜필드 신드롬’을 일으켰다.', '제롬 데이비드 샐린저 (지은이), 정영목 (옮긴이)', '민음사', 'https://image.aladin.co.kr/product/30882/22/cover200/k092831285_1.jpg', '국내도서>소설/시/희곡>영미소설', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('어마어마한 수학 - 수학에 미치는 6가지 이유', '“당신에게 수학의 문이 활짝 열리기를” 일본의 수포자들을 열광시킨 50만 부 수학책 저자 나가노 히로유키의 특별한 수학 강의.', '나가노 히로유키 (지은이), 김찬현 (옮긴이)', '동아시아', 'https://image.aladin.co.kr/product/29620/97/cover200/896262432x_1.jpg', '국내도서>과학>수학>수학 일반', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('삶이 허기질 때 나는 교양을 읽는다 - 하루 5분 지적인 나를 만드는 최고의 인문학 만찬', '“한번 읽기 시작하면 멈출 수 없다!” 3,000만 조회수가 입증한 읽을수록 빠져드는 인문학 완벽 교양서. 궁금했지만 어디서도 알 수 없었던 이야기, 이제껏 없던 신선한 접근으로 우리를 둘러싼 모든 것을 지식으로 만들어주는 국내 최고의 지식 채널 ‘지식 브런치’를 책으로 만난다.', '지식 브런치 (지은이)', '서스테인', 'https://image.aladin.co.kr/product/29615/49/cover200/k212838273_2.jpg', '국내도서>인문학>교양 인문학', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('아티스트 웨이, 마음의 소리를 듣는 시간 - 세상의 모든 소리에 귀 기울여 나를 바꾸는 법', '전 세계 500만 독자의 창조성을 깨우고 인생을 바꾼 ‘변화의 여왕’ 줄리아 캐머런이 신작 ≪아티스트 웨이, 마음의 소리를 듣는 시간≫으로 돌아왔다. 이 책은 ≪아티스트 웨이≫의 실천편으로 쉽고 단순하게 내 안의 창조성을 찾고 이를 통해 나를 바꾸는 핵심 방법을 소개한다. 바로 ‘내 안과 밖의 소리에 귀 기울이는 것’이다.', '줄리아 캐머런 (지은이), 이상원 (옮긴이)', '비즈니스북스', 'https://image.aladin.co.kr/product/29245/98/cover200/k772837296_1.jpg', '국내도서>자기계발>창의적사고/두뇌계발', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('보이지 않는 친구 2', '세계적 베스트셀러 《월플라워》의 작가 스티븐 크보스키의 신작 소설. 도시 외곽의 숲에서 엿새간 실종되었다 돌아온 크리스토퍼는 초자연적인 힘을 갖게 된다. 크리스토퍼는 인류를 악의 세력 아래 두려는 불온한 존재들에 맞서 세상을 구할 수 있을까?', '스티븐 크보스키 (지은이), 박아람 (옮긴이)', '북로드', 'https://image.aladin.co.kr/product/27819/29/cover200/k772734679_1.jpg', '국내도서>소설/시/희곡>호러.공포소설>외국 호러.공포소설', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('용의자 X의 헌신 - 제134회 나오키상 수상작', '히가시노 게이고 문학의 정수로 일컬어지는 &lt;용의자 X의 헌신&gt;이 새롭게 번역되어 출간됐다. 일본 문학 전문 번역가 양억관이 자신의 번역을 대폭 손질해 원작이 지닌 문학적 향기와 감동을 오롯이 되살려 냈다.', '히가시노 게이고 (지은이), 양억관 (옮긴이)', '재인', 'https://image.aladin.co.kr/product/11649/82/cover200/8990982707_1.jpg', '국내도서>소설/시/희곡>추리/미스터리소설>일본 추리/미스터리소설', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('루디 커피의 세계 세계의 커피 - 만화로 보는 지식사전', '만화로 보는 지식사전은 교과서적인 지식을 축적하는 것 이외에 소위 덕후레벨로 들어갈 수 있는 지식의 트리비아를 찾아가는 과정을 만화로 알아보는 시리즈이다. 첫 번째 책인 커피는 늘상 마시고 즐기는 커피를 덕후의 레벨에서 커피를 지식으로 알고 있는 사람들의 이야기이다.', '김재현 (지은이)', '봄봄스쿨', 'https://image.aladin.co.kr/product/9899/78/cover200/k052536762_2.jpg', '국내도서>요리/살림>술/음료/차>커피', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('카라마조프 가의 형제들 세트 - 전3권', '러시아의 대문호 도스토예프스키의 마지막 작품이자 최고작. 작가가 평생 동안 고민해 온 인간 존재의 근본 문제에 대한 모든 문학적 고민이 녹아들어 있는 소설이면서, 문학뿐 아니라 철학, 심리학, 종교를 아우르는 탁월한 저작이다.', '표도르 도스토옙스키 (지은이), 김연경 (옮긴이)', '민음사', 'https://image.aladin.co.kr/product/2090/83/cover200/s162835947_1.jpg', '국내도서>소설/시/희곡>러시아소설', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('불안 - 개정판', '일상의 철학자 알랭 드 보통이 파헤친 불안, 그 원인과 해법. ''불안''은 현대를 사는 우리에게는 매우 밀접한 개념이다. 알랭 드 보통의 말대로, 우리의 삶은 불안을 떨쳐내고, 새로운 불안을 맞아들이고, 또 다시 그것을 떨쳐내는 과정의 연속인지도 모른다. &lt;불안&gt;은 우리가 일상 속에서 겪는 다양한 종류의 불안 중 사회적 지위와 관련된 불안을 집중적으로 탐구하고 있다.', '알랭 드 보통 (지은이), 정영목 (옮긴이)', '은행나무', 'https://image.aladin.co.kr/product/1435/6/cover200/8956605599_2.jpg', '국내도서>에세이>외국에세이', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('서찰을 전하는 아이', '푸른숲 역사 동화 시리즈. &lt;봉주르, 뚜르&gt;로 문학동네어린이문학상을 수상한 작가 한윤섭의 첫 역사 동화. 동학 농민 운동으로 온 나라가 시끌시끌했던 1894년, 중요하고 비밀스러운 서찰을 전하기 위해 홀로 길을 떠난 열세 살 아이의 이야기를 담고 있다. 탄탄한 구성, 간결하고도 세련된 문체, 오묘한 분위기를 자아내는 장면 묘사로 어린이들의 시선을 강렬하게 잡아끈다.', '한윤섭 (지은이), 백대승 (그림), 전국초등사회교과모임 (감수)', '푸른숲주니어', 'https://image.aladin.co.kr/product/1371/69/cover200/897184664x_1.jpg', '국내도서>어린이>동화/명작/고전>국내창작동화', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('아가씨 돌보기 ~영애들이 다니는 명문 학교에서 제일가는 아가씨(생활력 없음)를 남몰래 돕는 시중 담당이 되었습니다 6 - Novel Engine', '히나코가 ‘사랑’을 깨닫게 된 여름 방학이 끝나고, 마침내 맞이한 2학기. 그리고 학교에서 막을 올리는 것은 학생들끼리 경영자를 체험해 보는《매니지먼트 게임》. 경쟁자가 등장하면서도, 이츠키는 학생회를 목표로 순조롭게 실적을 올려 나간다. 아가씨들은 그런 이츠키를 지켜보는데….', '사카이시 유사쿠 (지은이), 미와베 사쿠라 (그림), JYH (옮긴이)', '영상출판미디어(주)', 'https://image.aladin.co.kr/product/34538/51/cover200/k862932054_1.jpg', '국내도서>소설/시/희곡>라이트 노벨>노블엔진(Novel Engine)', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('우리에게 남은 시간 46일 2', '시한부인 해인의 곁에서 슬퍼하며, 끝까지 사랑과 지지를 아끼지 않는 우현의 슬픈 사랑이야기를 담고 있다. 마지막까지 절대 포기하지 않는 그들의 사랑은 독자의 마음을 짙게 울릴 것이다.', '이설 (지은이)', '하이스트', 'https://image.aladin.co.kr/product/34530/53/cover200/k692932952_1.jpg', '국내도서>소설/시/희곡>한국소설>2000년대 이후 한국소설', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('수퍼맘 박현영의 하루 15분 영어책 읽기의 기적', '16만 회원이 인정한 수퍼맘 박현영의 영어책 읽기 로드맵. 7개 국어를 구사하는 딸아이의 영어 말문이 빵 터질 수 있었던 결정적인 계기가 된 영어책 읽기 노하우와 로드맵을 연령에 따라 체계적으로 정리하여 한 권으로 엮은 것이다.', '박현영 (지은이)', '테라코타', 'https://image.aladin.co.kr/product/34481/72/cover200/k942932547_1.jpg', '국내도서>좋은부모>교육/학습>언어/외국어 교육', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('밥 챙겨 먹어요, 오늘도 행복하세요 - 15만 팔로워 올타임 레전드 마포농수산쎈타의 사계절 마음 편한 밥술친구 레시피', '출간 하루 만에 5천 부 돌파, 2주 만에 1만 부 돌파라는 대기록을 세운 요리책계의 혁명 『밥 챙겨 먹어요, 행복하세요』의 두 번째 레시피북이 출간되었다. ‘순두부열라면’ 창시자이자 트위터(현 엑스) 15만 팔로워가 열광 중인 자타공인 레시피 전도사 ‘마포농수산쎈타’가 우리의 사계절 입맛을 책임지러 돌아왔다.', '마포농수산쎈타 (지은이)', '위즈덤하우스', 'https://image.aladin.co.kr/product/34474/45/cover200/k242932633_1.jpg', '국내도서>요리/살림>생활요리', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('내가 대답하는 너의 수수께끼 - 아케가미 린네는 틀리지 않아', '가미시로 교스케의 장편소설 『내가 대답하는 너의 수수께끼 아케가미 린네는 틀리지 않아』가 출간되었다. 라이트노벨풍의 본격 미스터리로, 경쾌한 러브 코미디와 정통 본격 미스터리가 동시에 참신한 매력을 발산한다. 라이트노벨과 본격 미스터리가 합쳐져 발휘하는 시너지가 관건이다.', '가미시로 교스케 (지은이), 이연승 (옮긴이)', '블루홀식스(블루홀6)', 'https://image.aladin.co.kr/product/34448/16/cover200/k352932330_1.jpg', '국내도서>소설/시/희곡>추리/미스터리소설>일본 추리/미스터리소설', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('움직임의 법칙 - 무너진 체형을 바로잡는 내 몸 사용 설명서', '지면과 가장 가까운 발바닥부터 무릎, 허리, 등/어깨로 올라오며 몸의 움직임을 살펴보도록 구성하고, 몸을 구성하고 있는 근육에 대한 설명과 100여 컷의 일러스트를 더해 내용을 보다 쉽게 풀어내고자 했다.', '운동하는 데이브(최충식) (지은이)', '가나출판사', 'https://image.aladin.co.kr/product/34435/73/cover200/k862932228_1.jpg', '국내도서>건강/취미>건강운동>요가/체조/스트레칭', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('책벌레의 하극상 제5부 : 여신의 화신 6 - 사서가 되기 위해서라면 뭐든지 할 수 있어, V+', '‘로제마인과 빌프리트의 약혼을 취소하고 왕이 왕녀가 된다’는 충격적인 보고를 들고 온 로제마인 때문에 에렌페스트의 영주 일족은 혼란에 빠지게 된다. 하지만 결정은 번복할 수 없다.', '카즈키 미야 (지은이), 시이나 유우 (그림), 김정규 (옮긴이)', '길찾기', 'https://image.aladin.co.kr/product/34399/51/cover200/k602932517_1.jpg', '국내도서>소설/시/희곡>라이트 노벨>기타 라이트노벨', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('90분 너머 축구 이야기 - 사람, 역사, 가족, 친구', '90분 안에 담을 수 없는 축구 감동 스토리. 유럽에서 박지성을 응원했던 Kei는 이제 토트넘에서 활약하는 손흥민뿐 아니라 세계 최고 리그에서 달리고 있는 선수들에게서 인생과 가족, 역사와 삶의 자세를 읽어내고 있다.', 'Kei (지은이)', '끌리는책', 'https://image.aladin.co.kr/product/34367/84/cover200/k512932006_1.jpg', '국내도서>건강/취미>구기>축구', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('2025 전기기능사 필기 초단기 CBT 10개년 기출문제집 1400문제 - 저자직강 무료강의 제공', '전기설비와 전기기기의 단순 암기형 주제들을 전면에 배치하여 전기 설비와 전기기기에서 반드시 기본을 다져 50점 이상을 획득하고, 학습 도중에 시간부족으로 전기이론 계산문제를 다 공부하지 못했더라도 실제 시험장에서 충분히 합격권 점수를 획득할 수 있도록 전략적으로 구성하였다.', '파이팅혼공TV 컨텐츠 개발팀 (지은이)', '종이향기(인성재단)', 'https://image.aladin.co.kr/product/34235/90/cover200/k472931333_1.jpg', '국내도서>수험서/자격증>국가기술자격>전기>전기기능사/기사/산업기사', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('서로에게 들려주는 따뜻한 말', '독자들의 입소문만으로 베스트셀러에 오른 《나에게 들려주는 예쁜 말》의 뒤를 이어 두 번째 그림책이 나왔다. 관계를 맺기 시작하는 아이들의 마음을 두드리며, 자기 자신과 주변 사람들에게 어떻게 말해야 할지 보여준다.
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('채사장의 지대넓얕 10 : 거인의 어깨 - 지적 대화를 위한 넓고 얕은 지식', '과학의 역사를 짚어 본다. 고대에서 근대에 이르는 과학의 흐름을 검토하면서 그동안 과학이 어떤 성과를 만들어 왔는지, 어떤 인물이 시대와 투쟁하며 과학을 지금의 반열에 올려놓았는지를 살펴볼 것이다.', '채사장, 마케마케 (지은이), 정용환 (그림)', '돌핀북', 'https://image.aladin.co.kr/product/34082/27/cover200/k642931088_2.jpg', '국내도서>어린이>사회/역사/철학>정치/경제/법', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('미래의 손', '시인 차도하의 첫 시집이자 유고 시집으로, 총 62편의 시가 담겼다. 한계 없는 상상과 용기, 그리고 사랑으로 삶의 모든 순간을 열렬히 껴안는다. 뜨겁고 진실한 시인의 전심전력을 느낄 수 있다.', '차도하 (지은이)', '봄날의책', 'https://image.aladin.co.kr/product/34070/10/cover200/k252931478_1.jpg', '국내도서>소설/시/희곡>시>한국시', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('친애하는 슐츠 씨 - 오래된 편견을 넘어선 사람들', '살아 있는 호기심으로 한국에는 거의 알려지지 않은 뉴스를 발굴하고 배경 지식과 맥락까지 더해 대중에게 알려온 〈오터레터〉의 발행인 박상현이 우리 안의 차별과 해묵은 인식을 바꿀 도끼 같은 이야기를 들려준다.', '박상현 (지은이)', '어크로스', 'https://image.aladin.co.kr/product/34029/98/cover200/k152931161_1.jpg', '국내도서>인문학>교양 인문학', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('감정 기복이 심한 편입니다만 (불안 에디션)', '롤러코스터 같은 감정을 차분히 만들어주는 쉬운 기분 관리법과 내 삶을 돌아볼 수 있는 질문들이 담겨있다. 우리가 왜 나의 감정을 알아야 하고, 어떻게 하면 불안과 우울감을 벗어날 수 있는지에 대한 명확한 해답은 감정 기복이 심한 당신에게 아주 시원한 통쾌함과 교훈을 선사해줄 것이다.', '박한평 (지은이)', '딥앤와이드(Deep&WIde)', 'https://image.aladin.co.kr/product/33989/48/cover200/k082930546_1.jpg', '국내도서>에세이>한국에세이', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('똥볶이 할멈 6 - 학교 앞은 우리가 지킨다!', '햇살 초등학교 앞 ''방과 후 할멈 떡볶이'' 바로 옆에 ''방과 후 할망 문방구''가 들어서자 아이들은 떡볶이 가게를 지나쳐 우르르 문방구로 향한다. 손님이 없어 파리만 쫓는 신세가 된 똥볶이 할멈은 조수 치즈와 함께 몰래 문방구를 염탐하는데….', '강효미 (지은이), 김무연 (그림)', '슈크림북', 'https://image.aladin.co.kr/product/33928/37/cover200/k762930138_2.jpg', '국내도서>어린이>동화/명작/고전>국내창작동화', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('여행의 이유 (개정증보판) - 초판한정 각양장 + 면지 친필 사인(인쇄) 일러스트 + 책갈피 (작가 낭독 음성 QR코드)', '2019년 출간 직후 독자들의 열렬한 호응 속에 읽혀온 김영하 산문 『여행의 이유』 개정증보판이 복복서가에서 출간된다. 코로나 팬데믹 이후 우리의 일상에서 여행이란 과연 어떤 것이었는지, 김영하만의 현란하면서도 명쾌한 사유의 경로를 통해 비로소 이해해보게 되는 글 「여행이 불가능한 시대의 여행법」이 추가된 이번 『여행의 이유』 개정증보판은 김영하 산문의 정수로 불릴 만하다.', '김영하 (지은이)', '복복서가', 'https://image.aladin.co.kr/product/33763/31/cover200/k312930064_3.jpg', '국내도서>에세이>한국에세이', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('니체 인생수업 - 니체가 세상에 남긴 66가지 인생지혜', '작고한 지 2세기가 지난 오늘날까지도 니체의 말들은 빛이 바래지기는 커녕 뛰어난 통찰력과 날카로움으로 우리를 매혹시키고 있다. 역사상 가장 위대한 철학자 중 하나로 꼽히는 프리드리히 니체의 글 가운데, 엄선하고 또 엄선한 66편의 명언을 담은 책이다.', '프리드리히 니체 (지은이), 김지민 (엮은이)', '하이스트', 'https://image.aladin.co.kr/product/33672/40/cover200/s792931889_1.jpg', '국내도서>인문학>서양철학>현대철학>프리드리히 니체', 1, 1);
