INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('선재 업고 튀어 1~2 세트 - 전2권 - 이시은 작가, 변우석·김혜윤·송건희·이승협 배우 사인, 메시지 수록, 양장', 'tvN 드라마 ''선재 업고 튀어'' 작가판 무삭제 대본집으로, 이시은 작가의 촘촘한 구성과 상세한 지문, 따듯한 대사들을 원본 그대로 확인할 수 있다. 더불어, ‘작가의 에필로그’는 대본집 출간이 결정된 후 작가가 새로 집필한 씬으로 팬들에게 큰 선물이 될 것이다.', '이시은 (지은이)', '북로그컴퍼니', 'https://image.aladin.co.kr/product/34015/28/cover200/k982931060_1.jpg', '국내도서>예술/대중문화>영화/드라마>시나리오/시나리오작법', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('드롭 더 비트', '찬란이는 늘 피아노를 쳐 왔다. 피아노를 전공하겠다는 꿈을 가진 것은 아니지만, 찬란이에게 피아노는 공부에 지친 일상에서 하나뿐인 탈출구다. 모범생인 찬란이는 피아노뿐만 아니라 공부도 꽤 잘하는 편이어서, 아빠는 찬란이가 의사가 되기를 바란다.', '주봄 (지은이), 임나운 (그림)', '북멘토(도서출판)', 'https://image.aladin.co.kr/product/33991/43/cover200/896319583x_1.jpg', '국내도서>어린이>동화/명작/고전>국내창작동화', 1, 1);
