INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('읽으면서 바로 써먹는 어린이 감정 표현', '먼 외계 행성에서 온 침입자들이 지구 어린이들의 풍부한 감정을 빼앗기 위해 벌이는 소동이 담겨 있다. 지구를 지키기 위해 모험을 떠나는 찹이 패밀리와 함께, 200가지 알록달록한 무지갯빛 감정 단어를 배워 보자. 내 감정을 제대로 알고 멋지게 표현할 수 있게 된다.', '한날 (지은이)', '파란정원', 'https://image.aladin.co.kr/product/33850/81/cover200/k902930106_1.jpg', '국내도서>어린이>책읽기/글쓰기>글쓰기 일반', 1, 1);
