INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('사장학개론', '한국과 미국, 전 세계를 오가며 ‘사장을 가르치는 사장’으로 알려진 『돈의 속성』의 저자 김승호 회장의 신간이다. 평생 사장으로 살아온 그의 경영철학 모두를 10여 년에 걸쳐 정리해 온 그는, 이번 『사장학개론』 책을 통해 120가지 주제로 그 내용을 모두 담아 완성했다.', '김승호 (지은이)', '스노우폭스북스', 'https://image.aladin.co.kr/product/31325/1/cover200/k832832683_3.jpg', '국내도서>경제경영>기업 경영>경영 일반', 1, 1);
