INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('갈매기에게 나는 법을 가르쳐준 고양이', '라틴 문학권의 대표적 작가 루이스 세뿔베다가 쓴 철학 동화의 고전이다. 갈매기와 고양이를 주인공으로 내세워, 낯선 존재들이 약속을 지켜나가는 과정을 통해 하나의 존재로 화합해가는 여정을 간결한 문체로 그려내고 있다.', '루이스 세풀베다 (지은이), 이억배 (그림), 유왕무 (옮긴이)', '바다출판사', 'https://image.aladin.co.kr/product/28218/82/cover200/k812835371_1.jpg', '국내도서>어린이>동화/명작/고전>외국창작동화', 1, 1);
