INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('시소 인생 - 현실과 이상, 현재와 미래, 타인과 나 그 사이, 어딘가', '&lt;가볍지만 가볍지 않은&gt;, &lt;이러지도 저러지도 못하는 당신에게&gt;, &lt;내가 잘 하고 있는 건지 잘 모르겠습니다&gt;를 이은 강주원 작가의 네 번째 산문집. 작가의 문체는 화려하지 않다. 지극히 평범한, 보통의 문장으로 독자와 소통한다.', '강주원 (지은이)', '비로소(도서출판)', 'https://image.aladin.co.kr/product/27507/84/cover200/k922733199_1.jpg', '국내도서>에세이>한국에세이', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('이파라파냐무냐무 - 2021 볼로냐 라가치상 코믹-유아 그림책 부문 대상 수상작', '마시멜로가 사는 평화로운 마을. 풍요로운 먹거리와 폭신한 땅, 느긋해서 잠이 솔솔 올 것만 같은 마을의 동산 너머로 어느 날 천둥 같은 소리가 들려온다. 이파라파냐무냐무… 이파라파냐무냐무. 소리는 점점 가까이 들려오는데….', '이지은 (지은이)', '사계절', 'https://image.aladin.co.kr/product/26867/57/cover200/k182730301_1.jpg', '국내도서>유아>그림책>나라별 그림책>한국 그림책', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('하나님의 열심 - 믿음은 어디서 오는가', '박영선 목사의 대표작으로 잘 알려진 설교집의 개정판이다. 젊은 시절, 저자는 ‘믿음’이라는 주제를 놓고 집요한 질문과 고민으로 끈질기게 매달렸고 성경을 파헤치는 거침없는 추적 끝에 ‘믿음은 하나님의 열심이 빚어낸 결과’라는 답을 얻게 된다.', '박영선 (지은이)', '무근검(남포교회출판부)', 'https://image.aladin.co.kr/product/12724/78/cover200/k372532274_1.jpg', '국내도서>종교/역학>기독교(개신교)>기독교(개신교) 목회/신학>설교/성경연구', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('식탁 위의 세계사', '제2회 창비청소년도서상 교양 부문 대상 수상작. 우리 곁의 친근한 먹을거리를 통해 세계사의 중요한 사건들과 인물들에 관한 이야기로 안내하는 흥미로운 청소년 교양서다. 감자에서 비롯한 아일랜드 대기근부터 옥수수에 대한 러시아 지도자 흐루쇼프의 열정, 소금법에 저항한 간디의 소금 행진 등 식재료에 관계된 열 가지 이야기를 담았다.', '이영숙 (지은이)', '창비', 'https://image.aladin.co.kr/product/1726/29/cover200/8936452053_1.jpg', '국내도서>청소년>청소년 역사', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('만복이네 떡집', '아이들의 일상을 재치와 풍부한 상상력으로 풀어내는 동화작가 김리리의 신작. 이번 동화는 집에서 부족함 없이 자랐지만 자기도 모르게 나쁜 말과 행동을 툭툭 내뱉고 마는 만복이가 신비한 떡집을 만나 겪는 따듯하고 흥겨운 변화를 담고 있다.', '김리리 (지은이), 이승현 (그림)', '비룡소', 'https://image.aladin.co.kr/product/716/52/cover200/8949161346_2.jpg', '국내도서>어린이>동화/명작/고전>국내창작동화', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('체공녀 연대기 1931~2011', '식민지 시기 평양의 을밀대에 올라 고공 농성을 벌인 강주룡부터 2011년 부산의 35미터 크레인 위에 오른 김진숙에 이르기까지 한 세기에 걸친 공장 여성 노동자들의 끈질긴 투쟁사를 통해 그간 제대로 자리매김되지 않았던 여성 노동운동의 기억들을 복원하고 한국의 산업화와 노동운동의 역사를 페미니즘적 관점에서 다시 쓴다.', '남화숙 (지은이), 남관숙 (옮긴이)', '후마니타스', 'https://image.aladin.co.kr/product/34499/65/cover200/8964374622_1.jpg', '국내도서>역사>한국근현대사>일제치하/항일시대', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('0000', '소설집 《유령의 마음으로》 《초록은 어디에나》를 발표하고, 2023년 김유정작가상을 수상한 작가 임선우의 《0000》이 위즈덤하우스의 단편소설 시리즈 위픽으로 출간되었다. 소설은 “통장 잔고 0, 인간관계 0, 행동반경 0킬로미터, 메신저 알림 0”인 주인공의 외롭고도 고요한 죽음에서부터 시작된다.', '임선우 (지은이)', '위즈덤하우스', 'https://image.aladin.co.kr/product/34474/50/cover200/k422932633_1.jpg', '국내도서>소설/시/희곡>한국소설>2000년대 이후 한국소설', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('건축자 바울: 공간, 시간, 의례', '바울에 따르면 교회 곧 하나님의 신전이자 에클레시아는 세상과 구별된 공간이며, 세상과는 다른 질서가 작동한다. 또한 세상의 시간과는 질적으로 다른 시간이 흐른다. 그리고 그 중심에는 세례나 주의 만찬과 같은 의례가 있다. 성실하고 탁월한 연구로 빚어진 이 책을 통해 독자는 바울이 전한 교회, 예수의 복음이 빚어낸 교회의 본 모습에 더 가까이 다가갈 수 있을 것이다.', '정동현 (지은이)', '도서출판 학영', 'https://image.aladin.co.kr/product/34454/69/cover200/k392932430_1.jpg', '국내도서>종교/역학>기독교(개신교)>성경의 이해', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('어쨌든 귀여워 21 - S코믹스', '교마루 저택에서 돌아온 유자키 부부. 사랑이 가득한 달콤한 나날도 잠시 카구야가 부부의 비밀을 파헤치러 왔다! 주로 꽁냥거리는 부분에 대해…?!', '하타 켄지로 (지은이), 옷무 (옮긴이)', '㈜소미미디어', 'https://image.aladin.co.kr/product/34445/20/cover200/k112932236_1.jpg', '국내도서>만화>코믹/명랑만화', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('2025 김중규 여다나 압축 선행정학', '2023 하반기와 2024 상반기의 신경향 출제경향을 완벽 분석·반영하였다. 수험생들의 소중한 의견을 반영하여 가독성과 디테일을 더욱 높였으며, 최근 여러차례에 걸쳐 개정된 다수 법령들을 완벽하게 분석하여 특별자치도의 자치계층, 공무원 노조전임자의 지위와 보수 등의 내용을 반영하였다.', '김중규 (지은이)', '카스파(주)', 'https://image.aladin.co.kr/product/34434/41/cover200/k042932126_1.jpg', '국내도서>수험서/자격증>공무원 수험서>7/9급 공무원>행정학>7/9급 교재', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('삶이 고통일 땐 타인을 사랑하는 게 좋다 - 나를 구하는 인간관계의 과학', '공감하는 사람은 덜 공감하는 사람보다 더 행복하다. 몸과 마음의 면역 체계가 튼튼해 병에 잘 걸리지 않고, 심리적 안녕감이 높고, 더 오래 살며, 친밀한 관계 속에서 만족스러운 삶을 누린다. 눈에 보이지도 않는, 실체 없는 ‘공감’이 어떻게 이런 결과를 가져오는 걸까?', '앤서니 마자렐리, 스티븐 트리지악 (지은이), 소슬기 (옮긴이)', '윌북', 'https://image.aladin.co.kr/product/34371/78/cover200/k432932104_1.jpg', '국내도서>인문학>심리학/정신분석학>교양 심리학', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('2025 조태정 영어 리그래머(#Re-Grammar) 합격문법 125', '공무원 영어 시험에 빈출되는 문법 포인트 125개를 엄선하여 간결하고, 쉽게 학습할 수 있도록 구성한 문법 요약서다. 시험에 빈출되는 포인트를 125개로 정리하였고, 각 포인트에 중요도를 표시하여 공부의 방향성을 제시하였다.', '조태정 (지은이)', '메가스터디교육(공무원)', 'https://image.aladin.co.kr/product/34361/45/cover200/k352932903_1.jpg', '국내도서>수험서/자격증>공무원 수험서>7/9급 공무원>영어>7/9급 기본서', 1, 1);
