INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('로원의 말랑말랑 고양이와 소녀 컬러링북', '다채롭고 아름다운 색감으로 사랑받는 로원 작가의 두 번째 컬러링북. 소녀와 고양이가 함께하는 일상 속에서 느껴지는 다정하고 행복한 순간들을 담아, 독자들에게 따뜻하고 경쾌한 감성을 선사한다.', '로원 (지은이)', '그라데이션', 'https://image.aladin.co.kr/product/34247/91/cover200/k212931552_1.jpg', '국내도서>예술/대중문화>컬러링북', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('원칙 Principles (한정판 필사노트 에디션)', '출간 직후 전 세계적인 베스트셀러가 된 레이 달리오의 《원칙》. 대한민국의 독자에게도 뜨거운 사랑을 받으면서 8년 간 베스트셀러 자리를 유지하고 있다. 레이 달리오는 브리지워터 어소시에이츠 창립자로 ‘헤지펀드의 대부''라 불리며, 세계 0.001% 안에 드는 부의 거인이다.', '레이 달리오 (지은이), 고영태 (옮긴이)', '한빛비즈', 'https://image.aladin.co.kr/product/34213/58/cover200/k812931039_1.jpg', '국내도서>경제경영>기업 경영>경영 일반', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('2025 김중규 ALL바른 기출문제 선행정학 - 전3권', '여러 시험을 병행 준비하는 수험생 편의를 위해 9급, 7급, 국회, 경찰, 군무원, 공사·공단 등 다양한 행정학 시험을 모두 커버할 수 있도록, 각종 기출문제 경계를 뛰어넘어 정제·분석·수록하여 기본서 체제에 맞추어 통합하였으며 휴대하기 편하도록 3권으로 분권하였다.', '김중규 (지은이)', '카스파(주)', 'https://image.aladin.co.kr/product/34198/93/cover200/k742931518_2.jpg', '국내도서>수험서/자격증>공무원 수험서>7/9급 공무원>행정학>기출/문제집/모의고사', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('타키 포오 탐정 사무소 1 - 프랑스 파리에 나타난 괴도 물음표', '《타키 포오 코믹 어드벤처》, 《타키 포오의 이세계 여행사》, 《타키 포오 얼렁뚱땅 상식스쿨》 등 어린이 독자들의 사랑을 듬뿍 받고 있는 타키 포오 시리즈의 첫 ''세계 보물 찾기 학습만화''. 세계 보물을 훔치는 트레저 헌터 ‘괴도 물음표’를 쫓아 타키 포오와 함께 세계를 모험하는 이야기의 만화와 ‘타키의 역사 문화 상식’, ‘포오의 수사 일지’ 코너로 구성되었다.', '김언정 (지은이), 캐릭온TV (원작), 김규태 (그림)', '대원키즈', 'https://image.aladin.co.kr/product/34198/15/cover200/k262931415_2.jpg', '국내도서>어린이>학습만화', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('2025 이동기 영어 하루 프로젝트 : 프로젝트 1. Foundation 어휘강화 - 공무원 영어의 시작과 끝', '매일 부담 없는 학습량으로 꾸준한 영어 학습이 가능하도록 고안된 교재이다. 하루 10문제씩 일주일에 4번, 8주 동안 꾸준히 문제를 풀면서 이론을 실전에 적용하고 반복하여 학습 효과를 높일 수 있다. 이번 ‘어휘강화’ 편에서 2025년 9급 출제기조 전환에 따른 신경향 대비 문제를 수록하였다.', '(주)이앤미래(대표 이동기) (지은이)', '에스티유니타스', 'https://image.aladin.co.kr/product/34174/32/cover200/k712931114_1.jpg', '국내도서>수험서/자격증>공무원 수험서>7/9급 공무원>영어>단어/문법/독해 외', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('당신의 미래, ETF 투자가 답이다 - 10년 후, 후회하지 않는 현명한 선택', '18년간의 현장 경험과 깊이 있는 노하우를 바탕으로, 누구나 쉽게 이해하고 활용할 수 있는 ETF 투자 전략을 제시한다. 이 책은 단순한 투자 지침서를 뛰어넘어, 저자의 투자 철학과 ETF 실무자로서의 개인적인 이야기가 녹아 있는 에세이 형식으로 구성되어 있다.', '김남기 (지은이)', '북오션', 'https://image.aladin.co.kr/product/34169/61/cover200/896799821x_1.jpg', '국내도서>경제경영>재테크/투자>주식/펀드', 1, 1);
