INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('하루 10분 영어 그림책 100일 필사', '그림책은 수준이나 연령을 초월하는 감동과 힐링의 힘이 있다. 어린 아이들을 대상으로 하더라도 가만히 들여다보면 깊이 있는 인생 철학을 강하게 내뿜는다. 100일 필사를 위해 엄마의 마음, 그리고 교사의 시선을 녹여 책을 선정하고 다양한 삶의 장면마다 꼭 담아주고 싶은 가치들을 ‘버츄’로 분류했다.', '위혜정 (지은이)', '더블:엔', 'https://image.aladin.co.kr/product/33955/75/cover200/k902930435_1.jpg', '국내도서>외국어>영작문', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('169층 나무 집', '나무 집에는 어떤 언어든지 다른 언어로 옮길 수 있는 감자 통번역 기계, 언제든 어떤 날씨를 만들어 내는 전천후 기상 돔, 요술 거울 방이 새로 생긴다. 그러던 어느 날, 전천후 기상 돔에서 빠져 나간 날씨 탓에 시내가 엉망진창이 되어버리고 만다. 앤디와 테리, 질은 동물들과 함께 힘을 모아 겨우겨우 기상 돔 문을 닫지만, 그들에게 반갑지 않은 손님이 찾아오는데…….', '앤디 그리피스 (지은이), 테리 덴톤 (그림), 신수진 (옮긴이)', '시공주니어', 'https://image.aladin.co.kr/product/33854/6/cover200/k192930205_1.jpg', '국내도서>어린이>동화/명작/고전>외국창작동화', 1, 1);
