INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('나 없이는 존재하지 않는 세상 - 카를로 로벨리의 기묘하고 아름다운 양자 물리학', '≪시간은 흐르지 않는다≫, ≪모든 순간의 물리학≫ 의 저자 카를로 로벨리 최신작. 이 책은 북해의 섬 헬골란트에서 스물세 살의 독일 청년이 발견한 ‘양자론’에 대한 아이디어로 시작한다.', '카를로 로벨리 (지은이), 김정훈 (옮긴이), 이중원 (감수)', '쌤앤파커스', 'https://image.aladin.co.kr/product/32936/43/cover200/k692936048_2.jpg', '국내도서>과학>물리학>물리학 일반', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('네 느낌은 어떤 모습이니?', '아이가 자신의 감각, 감정, 생각, 느낌을 알아차리고 관찰하면서 내면을 차곡차곡 정리하고 다질 수 있도록 안내한다. 자기를 새롭게 발견할 뿐 아니라 다른 사람을 이해하고 서로 공감하는 특별한 경험을 하게 될 것이다.', '앤디 J. 피자, 소피 밀러 (지은이), 김세실 (옮긴이)', '불광출판사', 'https://image.aladin.co.kr/product/32855/68/cover200/k602936916_1.jpg', '국내도서>어린이>초등1~2학년>그림책', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('인생', '전 세계적으로 중국 현대문학을 대표하는 작가 위화의 등단 40주년을 맞아 대표작 《인생》, 《허삼관 매혈기》 개정판이 출간되었다. 1996년 국내 첫 출간 이후 지난 30여 년간 여러 세대를 통과하면서도 줄곧 큰 사랑을 받아온 두 작품을 새 모습으로 만나볼 수 있게 되었다.', '위화 (지은이), 백원담 (옮긴이)', '푸른숲', 'https://image.aladin.co.kr/product/32401/55/cover200/k152935989_1.jpg', '국내도서>소설/시/희곡>중국소설', 1, 1);
