INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('범도 2 - 봉오동의 그들', '신동엽문학상, 황순원문학상, 오영수문학상을 수상한 소설가 방현석이 다년간의 취재와 자료 조사를 거쳐 10년이 넘는 시간 동안 집필한 필생의 역작으로, 홍범도가 산짐승을 사냥하는 포수로서 산야를 떠돌다 항일 운동에 투신하여 각종 인간군상을 만나며 성장해나가는 과정을 그린 작품이다.', '방현석 (지은이)', '문학동네', 'https://image.aladin.co.kr/product/31747/89/cover200/8954693288_1.jpg', '국내도서>소설/시/희곡>역사소설>한국 역사소설', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('역행자 확장판 - 돈·시간·운명으로부터 완전한 자유를 얻는 7단계 인생 공략집', '95퍼센트의 인간은 타고난 유전자와 본성의 꼭두각시로 살아간다. 이들은 평생 돈, 시간, 운명에게 속박되어, 평범함을 벗어나지 못하고 불행하게 사는 ‘순리자’다. 그러나 5퍼센트의 인간은 다르다. 이들은 타고난 유전자의 본성을 역행해 돈, 시간, 운명으로부터 완전한 자유를 얻는다. 본성을 거슬러 행복을 쟁취하는 이들이 바로 ‘역행자’다.', '자청 (지은이)', '웅진지식하우스', 'https://image.aladin.co.kr/product/31730/9/cover200/k162936876_1.jpg', '국내도서>자기계발>성공>성공학', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('오백 년째 열다섯 2 - 구슬의 무게', '단군 신화와 우리 옛이야기에서 탄생한 야호족과 호랑족이라는 참신한 세계관, 두 족속이 최초 구슬을 두고 벌이는 구슬 전쟁이라는 흥미진진한 스토리, 그리고 오백 년을 열다섯으로 살아온 여자아이라는 독보적인 캐릭터로 독자들에게 사랑받은 『오백 년째 열다섯』 두 번째 이야기다.', '김혜정 (지은이)', '위즈덤하우스', 'https://image.aladin.co.kr/product/31132/36/cover200/k902831942_1.jpg', '국내도서>청소년>청소년 문학>청소년 소설', 1, 1);
