INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('정재승의 인류 탐험 보고서 9 : 농사로 세상을 바꾼 호미닌 - 어린이를 위한 호모 사피엔스 뇌과학', '뇌과학자 정재승 교수와 함께하는 두 번째 우리들의 이야기. 수많은 다른 유인원들이 아닌 오직 호미닌만이, 그중에서도 호모 사피엔스만이 오늘날과 같은 고도의 문명을 이루게 된 배경을 외계인의 시선으로 다시 한번 관찰해 본다.', '정재승, 차유진 (지은이), 김현민 (그림), 백두성 (감수)', '아울북', 'https://image.aladin.co.kr/product/34348/73/cover200/8950996588_1.jpg', '국내도서>어린이>과학/수학/컴퓨터>생물과 생명', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('초등 필수 한국사 일력 365 (스프링) - 해피이선생의 교과서가 쉬워지는', '교육 현장에서 초등학생을 가르치는 교육자이자 누적 조회수 500만 뷰에 달하는 초등교육 및 자녀교육 전문 유튜브 채널 ‘해피이선생’을 운영하고 있는 이상학 선생님이 한국사의 핵심 사건과 인물을 선별해서 365일 일력으로 완성했다.', '이상학(해피이선생) (지은이), 누똥바 (그림), 서울대학교 뿌리깊은 역사나무 (감수)', '한경키즈', 'https://image.aladin.co.kr/product/34331/29/cover200/8947549622_1.jpg', '국내도서>어린이>초등 한국사', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('이상한 과자 가게 전천당 : 마네키네코 도감', '&lt;이상한 과자 가게 전천당&gt; 시리즈를 더욱 재밌게, 세계관을 더욱 공고하게 만들어 줄 캐릭터북이 출간되었다. 바로, &lt;전천당&gt; 독자들에게 가장 사랑받는 캐릭터 ‘마네키네코’의 비밀스러운 정보를 한 권에 가득 담은 《이상한 과자 가게 전천당 : 마네키네코 도감》이다.', '히로시마 레이코 (지은이), 쟈쟈 (그림), 김정화 (옮긴이)', '길벗스쿨', 'https://image.aladin.co.kr/product/34323/82/cover200/k632932480_2.jpg', '국내도서>어린이>동화/명작/고전>외국창작동화', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('걷는 망자, ‘괴민연’에서의 기록과 추리', '민속학과 호러 그리고 본격 미스터리의 결합이라는 독창적인 작품 세계를 선보이며, 호러와 미스터리 장르 독자 모두에게 절대적인 지지를 얻고 있는 거장 ‘미쓰다 신조’의 새로운 시리즈, 《걷는 망자, ‘괴민연’에서의 기록과 추리》가 출간되었다.', '미쓰다 신조 (지은이), 김은모 (옮긴이)', '리드비', 'https://image.aladin.co.kr/product/34291/38/cover200/k552932174_1.jpg', '국내도서>소설/시/희곡>추리/미스터리소설>일본 추리/미스터리소설', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('꽁꽁꽁 댕댕', '강아지 꽁지의 배웅을 받으며 민지네 식구가 모두 집을 나선 아침. 띠리링, 띠리링, 띠리링…휴대폰 벨 소리에 냉장고 친구들이 모두 깨어난다. 민지 엄마가 서두르다 휴대폰을 냉장고에 두고 간 것이다. 냉장고 친구들이 전화를 받아 보니 민지가 다쳤으니 학교로 와 달라는 담임 선생님의 전화다.', '윤정주 (지은이)', '책읽는곰', 'https://image.aladin.co.kr/product/34278/15/cover200/k042932167_1.jpg', '국내도서>유아>그림책>나라별 그림책>한국 그림책', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('전자공시 모르면 주식투자 절대로 하지마라 - 전자공시생 범송공자의', '범송공자 장우진은 주식투자를 고려대학교 가치투자연구회 RISK에서 접했고 우여곡절 끝에 증권사의 애널리스트가 되었다. 그 파란만장한 경험을 바탕으로 2021년부터 네이버블로그 ‘전자공시생’을 시작했다. 그리고 오늘날까지 주요 전자공시를 분석하여 블로그에 게시하고 있으며, 명실상부 이 분야의 파워블로거가 되었다. 그런 그의 모든 공시 노하우를 한 권으로 집약한 책이 출간되었다.', '장우진 (지은이)', '베가북스', 'https://image.aladin.co.kr/product/34273/25/cover200/k452932065_1.jpg', '국내도서>경제경영>재테크/투자>주식/펀드', 1, 1);
