INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('국가는 어떻게 생각하는가 - 존 미어샤이머의 질문 : 외교 정책의 합리성, 이론에서 사례까지', '세계적 석학 존 미어샤이머가 국제정치학의 핵심 질문인 ‘국가는 합리적으로 행동하는가?’라는 근본적인 물음에 대한 획기적인 고찰을 제공한다. 제1차 세계대전부터 냉전 이후 미국의 동아시아 정책까지, 20가지 역사적 사례를 아우르는 책이다.', '존 J. 미어샤이머, 서배스천 로사토 (지은이), 권지현 (옮긴이), 옥창준 (해제)', '서해문집', 'https://image.aladin.co.kr/product/34257/11/cover200/k212932960_1.jpg', '국내도서>사회과학>정치학/외교학/행정학>세계패권과 국제질서', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('비인지 능력의 힘 - 흔들리지 않는 최상위권 아이들의 비밀', '10대 아이를 키우는 부모들이 알아야 할 중요한 정보를 담은 책이다. 특히 아이의 공부와 감정 발달이 서로 어떻게 연결되는지, 그리고 부모가 이 시기에 무엇을 어떻게 해줄 수 있는지에 대한 구체적인 조언을 담고 있다.', '모리구치 유스케 (지은이), 오시연 (옮긴이)', '길벗', 'https://image.aladin.co.kr/product/34251/6/cover200/k762931654_1.jpg', '국내도서>좋은부모>육아>육아법/육아 일반', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('개미대학 세력의 매집원가 구하기 완결판', '개인투자자들 사이에서 주식 교과서로 회자하고 있는 개미대학 주식투자 도서 시리즈의 완결판으로 1권 『개미대학 세력의 매집원가 구하기』와 2권 『주식초보 졸업하고 진짜 수익내자』의 핵심내용을 업그레이드 하였다.', '전석 (지은이)', '오베이북스', 'https://image.aladin.co.kr/product/34193/39/cover200/k432931315_1.jpg', '국내도서>경제경영>재테크/투자>주식/펀드', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('프루스트와 오징어 - 독서의 탄생부터 난독증까지, 책 읽는 뇌에 관한 모든 것', '세계적인 인지신경학자이자 아동발달학자인 매리언 울프의 대표작 《Proust and the Squid》가 재출간됐다. 2009년 한국에서 ‘책 읽는 뇌’라는 제목으로 출간되었던 이 책은 원제를 살려 《프루스트와 오징어》로 새롭게 이름을 달았다. 재출간을 맞아 한국어판 서문도 추가됐다.', '매리언 울프 (지은이), 이희수 (옮긴이)', '어크로스', 'https://image.aladin.co.kr/product/34152/40/cover200/k022931499_1.jpg', '국내도서>인문학>교양 인문학', 1, 1);
