INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('아무튼, 디지몬 - 길고도 매우 짧은 여름방학이 시작되었다', '아무튼 시리즈 67권. 악에 맞서 싸우는 아이들의 모험에 설레고, “상처받고 외롭고 두렵지만, 용기와 위로 한마디로 언제든 다시 진화할 수 있는 인물”들에게서 위로를 받았던 유년 시절의 두근거림과 슬픔에 관한 이야기이자 그런 슬픔을 통해 글쓰기로 나아가는, 지금의 천선란 세계의 시작에 관한 이야기이다.', '천선란 (지은이)', '위고', 'https://image.aladin.co.kr/product/34093/45/cover200/k382931180_1.jpg', '국내도서>에세이>한국에세이', 1, 1);
