INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('회복술사의 재시작 9 - ~즉사 마법과 스킬 카피의 초월 힐~, J Novel Next', '운명의 숙적 대포의 용사 브렛을 토벌하고 신생 지오랄 왕국의 왕이 된 케얄. 왕의 신분으로 세계 회의에 참석하지만, 그곳에는 지오랄에 반기를 들며 결탁한 이매망량들이 발호하고 있었다. 자국의 이익을 주장하며 모든 것을 빼앗아 가려는 위정자들. 사면초가에 몰린 상황에 의외의 구원자가 나타나는데…….', '츠키요 루이 (지은이), 시오콘부 (그림), 문기업 (옮긴이)', '서울문화사', 'https://image.aladin.co.kr/product/34554/83/cover200/k622933161_1.jpg', '국내도서>소설/시/희곡>라이트 노벨>제이 노벨 넥스트(J Novel Next)', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('소원을 빌려고 악마를 소환했는데 너무 예뻐서 결혼해 버렸습니다 1', '용병 길드에 속한 14살의 소년 테오 아스펠은 너무 약한 나머지, 파티에 들어갔다 쫓겨나기를 반복한다. 강해지고 싶은 마음에 어떤 소원이든 들어준다는 악마, 헬비를 소환한 테오. 그러나 그녀를 마주한 순간, 너무나 아름다운 모습에 그만 프러포즈해 버리고 마는데…?!', 'shiryu (지은이), 도나리케루 (그림)', '재담미디어', 'https://image.aladin.co.kr/product/34553/47/cover200/k792933168_1.jpg', '국내도서>만화>본격장르만화>SF/가상사회', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('스토리 혁명 - 숏폼 시대의 소비자를 사로잡는 스토리텔링 코드 9', '마케터부터 콘텐츠 제작자, 광고 기획자, 유튜브 크리에이터, SNS 인플루언서까지 모두가 ‘스토리’의 중요성을 아는 시대다. 그래서 책을 읽고 강의도 들으며 기승전결 완벽한 긴 서사가 담긴 스토리를 만들어낸다. 그런데 인내심도, 문해력도, 집중력도 저하된 시대의 소비자에게도 그 스토리가 통할까?', '현유석, 정종찬, 정다솔 (지은이)', '다산북스', 'https://image.aladin.co.kr/product/34545/8/cover200/k152933965_1.jpg', '국내도서>경제경영>마케팅/세일즈>마케팅/브랜드', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('사랑 후에 오는 것들 세트 - 전2권', '이세영, 사카구치 겐타로 주연 쿠팡플레이 오리지널 시리즈 &lt;사랑 후에 오는 것들&gt; 원작 소설. 공지영, 츠지 히토나리. 한일의 두 남녀 작가가 바다를 사이에 두고 남녀 주인공의 시선으로 써 내려간, 가슴 아프고 섬세한 사랑 이야기이다.', '공지영, 츠지 히토나리 (지은이), 김훈아 (옮긴이)', '(주)태일소담출판사', 'https://image.aladin.co.kr/product/34536/23/cover200/k662932058_2.jpg', '국내도서>소설/시/희곡>한국소설>2000년대 이후 한국소설', 1, 1);
