INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('중국필패 - 시험, 독재, 안정, 기술은 어떻게 중국을 성공으로 이끌었고 왜 쇠퇴의 원인이 되는가', '2018년 국가 주석 임기 제한이 폐지되면서 중국은 사실상 시진핑 1인 독재 체제로 돌입했다. 이후 중국은 세계 질서에 가히 위협적이라 할 수 있는 행적을 드러내고 있다. 우리는 중국을 이해할 수 있을까? 현 MIT 경영대학원 교수이자 중국-인도 연구센터 주임인 미국 내 중국 전문가 야성 황 교수는 과거의 문명국가, 현대의 문제국가 중국을 읽는 새로운 접근, ‘EAST 공식’을 제시한다.', '야성 황 (지은이), 박누리 (옮긴이)', '생각의힘', 'https://image.aladin.co.kr/product/34476/60/cover200/k722932637_1.jpg', '국내도서>사회과학>정치학/외교학/행정학>외교정책/외교학', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('리더는 항상 옳다 - 무엇이 옳은지 고민하는 만큼 리더는 성장한다', '스타트업 사원에서 다국적 IT기업 한국 대표로, 우미영이 전하는 리더를 위한 성장문답. 성공한 커리어란 무엇일까? 자신이 좋아하는 일을 찾고, 그것에 몰두하고, 그 과정에서 힘이 되는 동료를 얻고, 그들과 함께 성장하는 것 아닐까? 일하는 사람이면 누구나 꿈꾸는 모습이다.', '우미영 (지은이)', '북스톤', 'https://image.aladin.co.kr/product/34472/95/cover200/k892932630_1.jpg', '국내도서>자기계발>리더십>리더십', 1, 1);
