INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('천국대마경 10 - S코믹스', '한때, 타카하라 나라 시설이었던 부흥성에서, 의사와 다시 만난 마루와 키루코. 자신의 몸에 무슨 일이 있었는지를 듣고, 키루코의 머리 속에는 새로운 의문이 떠오른다. 한편, 거대 히루코. 안쥬라스와의 사투는, 더욱 격렬해 지면서 끝내 희생자를 내고 만다. 마루와 키루코의 선전도 허무하게 절체절명에 빠지는 가운데, 생각지 못한 인물이 원군으로 모습을 드러낸다.', '이시구로 마사카즈 (지은이), 천선필 (옮긴이)', '㈜소미미디어', 'https://image.aladin.co.kr/product/34399/36/cover200/k852932516_1.jpg', '국내도서>만화>본격장르만화>판타지>액션 판타지', 1, 1);
