INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('말하고 싶은 비밀 Vol.2', '누적 부수 68만 부 돌파, 동명의 영화 흥행까지 더해져 하이틴 로맨스 분야 베스트셀러로 자리매김한 《말하고 싶은 비밀》이 더 설레는 두 번째 이야기로 돌아왔다. 까칠한 완벽주의 여고생과 자유분방한 훈남 남고생, 정반대의 세계에 살던 두 사람이 또 한 번 ‘교환 일기’로 특별한 교집합을 만들어내는 두 번째 책 《말하고 싶은 비밀 vol.2》는 서툴지만 그래서 더 풋풋하고 애틋한 첫사랑을 세밀하고 사랑스럽게 그려냈다.', '사쿠라 이이요 (지은이), 김윤경 (옮긴이)', '모모', 'https://image.aladin.co.kr/product/34373/66/cover200/k622932307_1.jpg', '국내도서>소설/시/희곡>로맨스소설>외국 로맨스소설', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('스푸트니크의 연인', '청춘의 순수하고 비현실적인 사랑을 그리며 전 세계에 하루키 신드롬을 일으킨 『상실의 시대』와 신비한 사랑의 끝에서 구원을 찾는 『국경의 남쪽, 태양의 서쪽』에 이어 무라카미 하루키가 쓴 연애소설 3부작의 완결편.', '무라카미 하루키 (지은이), 임홍빈 (옮긴이)', '문학사상', 'https://image.aladin.co.kr/product/34366/28/cover200/8970120939_1.jpg', '국내도서>소설/시/희곡>일본소설>1950년대 이후 일본소설', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('아름답고 위험한 이름, 비너스', '일본 최고의 추리 소설 작가 히가시노 게이고의 숨은 걸작 《위험한 비너스》의 개정판이다. 막대한 유산 상속을 둘러싼 친족간의 갈등과 이 전 재산을 물려받을 당사자의 실종이라는, 추리 소설로서 다소 평범해 보이는 소재는 히가시노 게이고의 손에서 보란 듯이 비틀어진다.', '히가시노 게이고 (지은이), 양윤옥 (옮긴이)', '하빌리스', 'https://image.aladin.co.kr/product/34360/66/cover200/k942932900_1.jpg', '국내도서>소설/시/희곡>추리/미스터리소설>일본 추리/미스터리소설', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('린 마케팅 - 살아남고 싶으면 돈 쓰는 마케팅을 버려라', '마케팅의 전략을 다룬 저자의 전작 『1페이지 마케팅 플랜』의 실천 및 전술 편으로, 훨씬 더 구체적인 마케팅 기법을 소개하고 있다. 마케팅은 특출난 능력을 요구한다기보다 기본과 정확한 방향 설정 그리고 꾸준한 과정임을 저자는 재차 강조한다.', '앨런 딥 (지은이), 김나연 (옮긴이)', '알파미디어', 'https://image.aladin.co.kr/product/34334/25/cover200/k192932793_1.jpg', '국내도서>경제경영>마케팅/세일즈>마케팅/브랜드', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('2025 이기적 정보처리기능사 필기 기본서 & 기출공략집 - 최신 출제기준 반영 + 동영상 강의 무료 + CBT 온라인 문제집', '2025년 출제기준을 반영한 이기적 정보처리기능사 필기 기본서. 핵심이론으로 공부한 뒤에 이론의 예상문제로 이해도를 점검하고, 대표 기출 이론과 문제로 최종 복습을 하면서 이론을 충분히 공부하자.', '영진정보연구소 (지은이)', '영진.com(영진닷컴)', 'https://image.aladin.co.kr/product/34334/10/cover200/8931475969_1.jpg', '국내도서>컴퓨터/모바일>활용능력>정보처리기능사', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('인터뷰하는 법 - 당신이라는 이야기 속으로', '신문기자로, 인터뷰어로 다양한 사람들의 이야기를 19년째 인터뷰해온 장은교 작가의 경험과 노하우를 담은 책. 인터뷰 기획부터 섭외, 진행, 콘텐츠로 정리하는 법과 인터뷰 글쓰기, 최종 리뷰까지 세심하게 안내한다. 인터뷰이가 마음을 자연스럽게 열어 보일 수 있는 ‘열쇠 같은 질문’ 만드는 법을 비롯, 좋은 질문을 하고 싶은 사람들에게 도움이 될 힌트들도 듬뿍 담겨 있다.', '장은교 (지은이)', '터틀넥프레스', 'https://image.aladin.co.kr/product/34253/70/cover200/k862932761_1.jpg', '국내도서>예술/대중문화>예술/대중문화의 이해>대중문화론', 1, 1);
