INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('결혼·여름 - 태양, 입맞춤, 압생트 향… 청년 카뮈의 찬란한 감성', '『결혼 · 여름』은 카뮈 사상의 핵심인 ‘부조리’와 ‘반항’의 출발 및 완성 과정이 육성으로 들리는 듯한 자전적 기록이다. 하지만 수많은 이들을 벅차 오르게 했던 『결혼 · 여름』의 가장 큰 매력은 감각적이며 관능적인 문체다. 드물게 시와 사상, 예술과 철학이 완벽하게 결합된 에세이가 우리에게 닿았다.', '알베르 카뮈 (지은이), 장소미 (옮긴이)', '녹색광선', 'https://image.aladin.co.kr/product/32086/69/cover200/k372834096_1.jpg', '국내도서>에세이>외국에세이', 1, 1);
