INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('미움받을 용기 (200만 부 기념 스페셜 에디션) - 자유롭고 행복한 삶을 위한 아들러의 가르침', '자유로워질 용기, 평범해질 용기 그리고 ‘미움받을 용기’까지, 자유롭고 행복한 삶을 위한 아들러의 가르침을 ‘철학자와 청년의 대화’ 형식으로 엮어, ‘어떻게 행복한 인생을 살 것인가?’라는 인간 본연의 질문에 쉽고 명쾌한 해답을 제시해준다.', '기시미 이치로, 고가 후미타케 (지은이), 전경아 (옮긴이), 김정운 (감수)', '인플루엔셜(주)', 'https://image.aladin.co.kr/product/30782/55/cover200/k442831368_1.jpg', '국내도서>인문학>심리학/정신분석학>교양 심리학', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('혼자 공부하는 데이터 분석 with 파이썬 - 1:1 과외하듯 배우는 데이터 분석 자습서', '독학으로 데이터 분석을 배우는 입문자가 ‘꼭 필요한 내용을 제대로 학습’할 수 있도록 구성했다. 뭘 모르는지조차 모르는 입문자의 막연한 마음에 십분 공감하여 과외 선생님이 알려주듯 친절하게, 핵심적인 내용만 콕콕 집어 준다.', '박해선 (지은이)', '한빛미디어', 'https://image.aladin.co.kr/product/30707/1/cover200/k602830144_1.jpg', '국내도서>컴퓨터/모바일>컴퓨터 공학>데이터베이스 개론', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('나는 오래된 거리처럼 너를 사랑하고', '2000년 『문학과사회』로 등단한 이후 감각적인 은유와 선명한 이미지로 낡고 익숙한 일상을 재배치하는 한편 동시대의 현실에 밀착한 문제의식을 철학적 사유와 시적 정치성으로 풀어내온 진은영 시인이 10년 만에 시집 &lt;나는 오래된 거리처럼 너를 사랑하고&gt;를 펴냈다.', '진은영 (지은이)', '문학과지성사', 'https://image.aladin.co.kr/product/30036/43/cover200/8932040443_1.jpg', '국내도서>소설/시/희곡>시>한국시', 1, 1);
