INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('시계태엽 오렌지', '스탠리 큐브릭 감독의 영화로 유명한 문제작 <시계태엽 오렌지 A Clockwork Orange>가 재출간됐다. 1962년 영국에서 발표된 이 작품은 독특한 소재와 혁신적 언어, 철학적인 주제를 고루 갖추었다는 평가를 받으며 현대 영문학 고전의 반열에 올랐다.', '앤서니 버지스 (지은이), 박시영 (옮긴이)', '민음사', 'https://image.aladin.co.kr/product/53/66/cover200/8937461129_3.jpg', '국내도서>소설/시/희곡>영미소설', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('김대중 육성 회고록 (특별 보급판) - 김대중은 오늘 우리에게 무엇을 말하는가', '김대중 대통령의 탄생 100주년, 서거 15주기를 맞아 그의 육성으로 작성된 마지막 자서전 『김대중 육성 회고록』을 출간한다. 1924년 신안 하의도에서 출생했을 때부터 2009년 서거하기 전까지 김대중 대통령의 생애와 사상을 생생한 육성으로 들을 수 있다.', '연세대학교 김대중도서관 (지은이)', '한길사', 'https://image.aladin.co.kr/product/34552/99/cover200/8935678813_1.jpg', '국내도서>사회과학>정치학/외교학/행정학>정치인', 1, 1);
