INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('안녕, 나의 인형', '손바느질로 한 땀 두 땀 《안녕, 나의 인형》 안에는 꿈을 만나 꿈속에 사는 저자의 꿈같은 이야기가 담겼다. 그리고 20여 년 익힌 손바느질 인형 만드는 방법 중에 누구나 손쉽게 천천히 완성해 나갈 수 있는 17가지의 방법을 엄선했다.', '황윤숙 (지은이)', '여가로운삶', 'https://image.aladin.co.kr/product/34545/71/cover200/k312933967_1.jpg', '국내도서>건강/취미>공예>뜨개질/퀼트/십자수/바느질', 1, 1);
