INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('좁고 깊게 산다는 것에 관하여 - 뉴욕의 도시 속 수도승 단다파니에게 배우는 우리 삶에서 정말 중요한 것을 찾는 방법', '와이의 힌두교 수도원에서 10년 동안 수행을 한 뒤, 뉴욕으로 나와 세상 사람들에게 자신의 깨달음을 알리고 있는 도시 속 수도승 단다파니는 그 답으로 좁고 깊게 사는 삶을 제시한다. 그는 《좁고 깊게 사는 법에 관하여》에서 우리가 왜 좁고 깊은 삶을 살아야 하는지, 그렇게 살기 위한 도구는 어떤 것이 있는지, 또 그 도구를 어떻게 하면 효율적으로 사용할 수 있는지를 알려준다.', '단다파니 (지은이), 이소영 (옮긴이)', '위즈덤하우스', 'https://image.aladin.co.kr/product/34535/64/cover200/k772932956_1.jpg', '국내도서>자기계발>성공>성공학', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('캐리', '메인주의 소도시 챔벌레인. 기독교 광신자인 어머니와 단둘이 사는 캐리 화이트는 늘 학교에서 다른 아이들의 괴롭힘을 받던 고독하고 내성적인 열일곱 살 소녀다. 자라면서 성(性)에 관한 지식을 전혀 습득하지 못했던 캐리는 체육 수업 후 샤워실에서 초경을 경험하여 패닉에 빠져 버리고, 또래 여자애들의 비웃음과 괴롭힘으로 인한 스트레스 때문에 잠재해 있던 염력이 발현된다.', '스티븐 킹 (지은이), 한기찬 (옮긴이)', '황금가지', 'https://image.aladin.co.kr/product/34535/28/cover200/k452932956_1.jpg', '국내도서>소설/시/희곡>호러.공포소설>외국 호러.공포소설', 1, 1);
