INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('낯선 이야기는 우리 곁에 있다 - SF와 인류학이 함께 그리는 전복적 세계', '‘인류학의 렌즈로 SF 읽고 다시 쓰기’를 시도한 책이다. 인류학자 정헌목과 황의진은 『어둠의 왼손』, 『시녀 이야기』, 『솔라리스』 등 고전 SF뿐 아니라 김초엽과 배명훈 같은 오늘날 한국 SF를 대표하는 작가의 작품까지, 열한 편의 SF를 다양한 인류학 논의와 연결 지어 읽으며 한국 사회가 직면한 다양한 문제와도 긴밀하게 잇는다.', '정헌목, 황의진 (지은이)', '반비', 'https://image.aladin.co.kr/product/34527/89/cover200/k122932857_1.jpg', '국내도서>인문학>교양 인문학', 1, 1);
