INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('이네스는 오늘 태어날 거야 (문고본)', '2023년 국제부커상 최종 후보에 올랐던 멕시코 소설 『이네스는 오늘 태어날 거야』는 바로 여성에게 주어진 모성 선택의 문제를 본격적으로 다룬 작품이다. 주인공이자 화자인 라우라는 외국에서 공부하고 아무 부담 없이 연애를 하며 자유롭게 살아가는 비혼 여성이다.', '과달루페 네텔 (지은이), 최이슬기 (옮긴이)', '바람북스', 'https://image.aladin.co.kr/product/34441/9/cover200/k472932238_1.jpg', '국내도서>소설/시/희곡>스페인/중남미소설', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('어쩔뚱땡! 고구마머리TV 7 : 우주 탐험 3 - 외계 행성의 비밀 - 호기심·상상력이 쑥쑥 자라나는 과학학습만화', '외계인 3인방의 초대를 받아 쉬메카 행성으로 떠나게 된 고구마머리 탐험대. 그러나 쉬메카 행성에 도착하기도 전, 의문의 고양이와 마주친 뒤 고구마머리호를 잃어버리게 된다. 과연 고구마머리호는 어디에 있을까요? 그리고 의문의 고양이는 누구일까?', '서동건 (지은이), 이정태 (그림), 이명현 (감수)', '아울북', 'https://image.aladin.co.kr/product/34429/67/cover200/8950917483_1.jpg', '국내도서>어린이>과학/수학/컴퓨터>지구와 우주', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('‘위안부’, 더 많은 논쟁을 할 책임 - 민족주의와 망언의 적대적 공존을 넘어', '1991년 8월 14일 고(故) 김학순이 스스로 ‘위안부’임을 밝히고 피해를 공개 증언한 지 30년이 넘었다. 그전까지 모습을 드러내지 못했던 피해자들은 자신들의 존재를 알림으로써 일본의 전쟁범죄를 고발했고, 지금도 여전히 일본 정부의 공식적인 사과와 배상을 촉구하고 있다.', '권은선, 김신현경, 김은경, 김은실, 김주희, 박정애, 야마시타 영애, 이지은, 이혜령, 정희진, 허윤 (지은이), 김은실 (엮은이)', '휴머니스트', 'https://image.aladin.co.kr/product/34428/22/cover200/k792932129_1.jpg', '국내도서>사회과학>여성학/젠더>여성학이론', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('야무진 고양이는 오늘도 우울 9 - 포미포미', '전부터 고양이를 키우는 것에 관심이 많았던 사쿠의 직장 동료 오시로는, 사쿠와 함께 처음으로 고양이 카페를 체험해 보고 나서 푸욱 빠져버리고 말았다. 고양이 카페의 단골이 된 것을 직장에서는 티 내지 않고 있던 오시로였지만….', '야마다 히츠지 (지은이), Leigh (옮긴이)', '㈜소미미디어', 'https://image.aladin.co.kr/product/34399/38/cover200/k912932517_1.jpg', '국내도서>만화>동물만화', 1, 1);
