mapper.select:
SELECT return_date
FROM lend_tb
WHERE id = #{id}

SELECT id
FROM reservation_tb
WHERE status = 0 AND book_id = #{bookId}
ORDER BY reservation_date ASC
LIMIT 1;

mapper.update:
UPDATE lend_tb
SET returned_date = CURRENT_TIMESTAMP
WHERE id = #{id}

UPDATE reservation_tb
SET wait_date = DATEADD(DAY, 3, NOW())
WHERE id = #{id}

UPDATE book_tb
SET current_stock = current_stock + 1
WHERE id = #{bookId}
