mapper.insert:
INSERT INTO lend_tb (user_id, book_id, lend_date, return_date,
returned_date)
VALUES (#{userId}, #{bookId}, NOW(), DATEADD('DAY', 7, NOW()), NULL);

mapper.select:
SELECT COUNT(*)
FROM reservation_tb
WHERE book_id = #{bookId}
AND reservation_date < (
SELECT reservation_date
FROM reservation_tb
WHERE user_id = #{userId}
AND book_id = #{bookId}
)
AND status = 0
